CREATE OR REPLACE PROCEDURE ageint_inicia_confirmacao (cd_pessoa_fisica_p text, ie_iniciar_p text, nm_usuario_p text, nm_paciente_p text default null, ds_erro_p INOUT text DEFAULT NULL) AS $body$
DECLARE


qt_iniciado_w	bigint;
qt_iniciado_user_w	bigint;		
				    

BEGIN

if (ie_iniciar_p = 'S') then
	select	count(*)
	into STRICT	qt_iniciado_w
	from	ageint_confirm_agendamento
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	coalesce(ie_em_agenda,'N') = 'S';
	IF (qt_iniciado_w = 0) THEN
		select	count(*)
		into STRICT	qt_iniciado_w
		from	AGEINT_PAC_CONFIRM_AGENDA
		where	cd_pessoa_fisica = cd_pessoa_fisica_p
		and	coalesce(ie_em_agenda,'N') = 'S';
	end if;

	select	count(*)
	into STRICT	qt_iniciado_user_w
	from	ageint_confirm_agendamento
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	nm_usuario_em_agenda = nm_usuario_p
	and	coalesce(ie_em_agenda,'N') = 'S';
	IF (qt_iniciado_user_w = 0) THEN
		select	count(*)
		into STRICT	qt_iniciado_user_w
		from	AGEINT_PAC_CONFIRM_AGENDA
		where	cd_pessoa_fisica = cd_pessoa_fisica_p
		and	nm_usuario_em_agenda = nm_usuario_p
		and	coalesce(ie_em_agenda,'N') = 'S';
	end if;
	
	if (qt_iniciado_w > 0) and (qt_iniciado_user_w = 0) then
		ds_erro_p := WHEB_MENSAGEM_PCK.get_texto(354242,null);
	else
		if (coalesce(cd_pessoa_fisica_p::text, '') = '') then
			update	ageint_confirm_agendamento
			set	ie_em_agenda = 'S',
				nm_usuario_em_agenda = nm_usuario_p
			where	nm_paciente = nm_paciente_p;
			update	AGEINT_PAC_CONFIRM_AGENDA
			set	ie_em_agenda = 'S',
				nm_usuario_em_agenda = nm_usuario_p
			where	nm_paciente = nm_paciente_p;
		else
			update	ageint_confirm_agendamento
			set	ie_em_agenda = 'S',
				nm_usuario_em_agenda = nm_usuario_p
			where	cd_pessoa_fisica = cd_pessoa_fisica_p;
			update	AGEINT_PAC_CONFIRM_AGENDA
			set	ie_em_agenda = 'S',
				nm_usuario_em_agenda = nm_usuario_p
			where	cd_pessoa_fisica = cd_pessoa_fisica_p;
		end if;
	end if;
else
	 if (coalesce(cd_pessoa_fisica_p::text, '') = '') then
        update	ageint_confirm_agendamento
        set	ie_em_agenda = 'N',
            nm_usuario_em_agenda  = NULL
        where	nm_paciente = nm_paciente_p;
        update	AGEINT_PAC_CONFIRM_AGENDA
        set	ie_em_agenda = 'N',
            nm_usuario_em_agenda  = NULL
        where	nm_paciente = nm_paciente_p;
    else
        update	ageint_confirm_agendamento
        set	ie_em_agenda = 'N',
            nm_usuario_em_agenda  = NULL
        where	cd_pessoa_fisica = cd_pessoa_fisica_p;
        update	AGEINT_PAC_CONFIRM_AGENDA
        set	ie_em_agenda = 'N',
            nm_usuario_em_agenda  = NULL
        where	cd_pessoa_fisica = cd_pessoa_fisica_p;
     end if;
end if;
commit;

end;
$body$
