SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ageint_inserir_item_conv ( nr_seq_agenda_int_p bigint, nr_seq_agenda_item_p bigint, nm_usuario_p text) AS $body$
DECLARE

									 
cd_convenio_w				agenda_integrada_conv_item.cd_convenio%type;
cd_categoria_w				agenda_integrada_conv_item.cd_categoria%type;
cd_plano_w					agenda_integrada_conv_item.cd_plano%type;
cd_usuario_convenio_w		agenda_integrada_conv_item.cd_usuario_convenio%type;
dt_validade_carteira_w		agenda_integrada_conv_item.dt_validade_carteira%type;	
nr_seq_agenda_item_w		agenda_integrada_item.nr_sequencia%type;
nr_seq_interno_w			agenda_integrada_conv_item.nr_sequencia%type;

/* Busca todos os itens que não possuem convênio cadastrado. */
				 
C01 CURSOR FOR 
	SELECT 	coalesce(a.nr_sequencia,0) 
	from 	agenda_integrada_item	a		 
	where 	a.nr_seq_agenda_int = nr_seq_agenda_int_p 
			and a.nr_sequencia <> nr_seq_agenda_item_p 
			and	not exists (SELECT 1 from agenda_integrada_conv_item b where b.nr_seq_agenda_item = a.nr_sequencia);
				

BEGIN 
/* Busca os dados do convênio que esta sendo salvo. */
 
select	max(cd_convenio) cd_convenio, 
		max(cd_categoria) cd_categoria, 
		max(cd_plano) cd_plano,  
		max(cd_usuario_convenio) cd_usuario_convenio, 
		max(dt_validade_carteira) dt_validade_carteira 
into STRICT	cd_convenio_w, 
		cd_categoria_w, 
		cd_plano_w, 
		cd_usuario_convenio_w, 
		dt_validade_carteira_w 
from	agenda_integrada_conv_item 
where	nr_seq_agenda_item	= (SELECT max(x.nr_sequencia) 
			from agenda_integrada_item	x		 
			where x.nr_seq_agenda_int = nr_seq_agenda_int_p 
			and	 x.nr_sequencia = nr_seq_agenda_item_p 
			and	 exists (select 1 from agenda_integrada_conv_item z where z.nr_seq_agenda_item = x.nr_sequencia));		
	 
open C01;
loop 
fetch C01 into 
	nr_seq_agenda_item_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	if (nr_seq_agenda_item_w > 0) then 
		select	nextval('agenda_integrada_conv_item_seq') 
		into STRICT	nr_seq_interno_w 
		;
	 
		insert into agenda_integrada_conv_item( 
			nr_sequencia, 
			dt_atualizacao, 
			nm_usuario, 
			nr_seq_agenda_item, 
			cd_convenio, 
			cd_categoria, 
			cd_plano, 
			cd_usuario_convenio, 
			dt_validade_carteira 
		) values ( 
			nr_seq_interno_w, 
			clock_timestamp(), 
			nm_usuario_p, 
			nr_seq_agenda_item_w, 
			cd_convenio_w, 
			cd_categoria_w, 
			cd_plano_w, 
			cd_usuario_convenio_w, 
			dt_validade_carteira_w 
		);
	end if;
	CALL ageint_atual_item_conv(nr_seq_agenda_item_w, null, nm_usuario_p);
	 
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
