CREATE OR REPLACE PROCEDURE ageint_nova_agenda_noshow_mmed ( nr_seq_ageint_p bigint, nr_seq_p INOUT bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_agenda_int_w	bigint;
nr_seq_status_w		bigint;
cd_pessoa_fisica_w	varchar(10);
nm_paciente_w		varchar(60);
cd_convenio_w		integer;
cd_categoria_w		varchar(10);
cd_plano_w		varchar(10);
cd_usuario_convenio_w	varchar(30);
nr_telefone_w		varchar(60);
dt_nascimento_w		timestamp;
qt_altura_cm_w		real;
cd_estabelecimento_w	bigint;

BEGIN
if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (nr_seq_ageint_p IS NOT NULL AND nr_seq_ageint_p::text <> '') then
	select	cd_pessoa_fisica,
		nm_paciente,
		cd_convenio,
		cd_categoria,
		cd_plano,
		cd_usuario_convenio,
		nr_telefone,
		dt_nascimento,
		qt_altura_cm,
		cd_estabelecimento
	into STRICT	cd_pessoa_fisica_w,
		nm_paciente_w,
		cd_convenio_w,
		cd_categoria_w,
		cd_plano_w,
		cd_usuario_convenio_w,
		nr_telefone_w,
		dt_nascimento_w,
		qt_altura_cm_w,
		cd_estabelecimento_w
	from	agenda_integrada
	where	nr_sequencia = nr_seq_ageint_p;

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_status_w
	from	agenda_integrada_status
	where	ie_status_tasy	= 'EA'
	and	coalesce(ie_situacao, 'A')	= 'A';

	if (nr_seq_status_w = 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(218757);
	end if;

	select	nextval('agenda_integrada_seq')
	into STRICT	nr_seq_agenda_int_w
	;

	insert into agenda_integrada(
		nr_sequencia,
		dt_inicio_agendamento,
		cd_pessoa_fisica,
		cd_convenio,
		cd_categoria,
		cd_plano,
		cd_usuario_convenio,
		nr_telefone,
		dt_nascimento,
		qt_altura_cm,
		cd_estabelecimento,
		ie_turno,
		dt_atualizacao,
		nm_usuario,
		nr_seq_status,
		nm_paciente)
	values (
		nr_seq_agenda_int_w,
		clock_timestamp(),
		cd_pessoa_fisica_w,
		cd_convenio_w,
		cd_categoria_w,
		cd_plano_w,
		cd_usuario_convenio_w,
		nr_telefone_w,
		dt_nascimento_w,
		qt_altura_cm_w,
		cd_estabelecimento_w,
		'2',
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_status_w,
		nm_paciente_w);

	nr_seq_p := nr_seq_agenda_int_w;
end if;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
