CREATE OR REPLACE PROCEDURE ageint_obter_item_de_para ( nr_Seq_item_grupo_p bigint, cd_estab_destino_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, nr_seq_item_ret_p INOUT bigint) AS $body$
DECLARE


qt_regra_pri_w		bigint;
qt_regra_seg_w		bigint;
nr_seq_item_pri_w		bigint;
nr_seq_item_seg_w		bigint;


BEGIN


select	count(*)
into STRICT	qt_regra_pri_w
from	ageint_item_grupo_de_para
where	nr_seq_item_pri	= nr_seq_item_grupo_p;

if (qt_regra_pri_w > 0) then
	select	max(d.nr_seq_item_seg)
	into STRICT	nr_seq_item_pri_w
	from	agenda_int_area a,
			agenda_int_grupo b,
			agenda_int_grupo_item c,
			ageint_item_grupo_de_para d
	where	a.nr_sequencia			= b.nr_seq_area
	and		b.nr_sequencia			= c.nr_seq_grupo
	and		c.nr_sequencia			= d.nr_seq_item_seg
	and		d.nr_seq_item_pri		= nr_seq_item_grupo_p
	and		a.cd_estabelecimento	= cd_estab_destino_p;
end if;

select	count(*)
into STRICT	qt_regra_seg_w
from	ageint_item_grupo_de_para
where	nr_seq_item_seg	= nr_seq_item_grupo_p;

if (qt_regra_seg_w > 0 ) then
	select	max(d.nr_seq_item_pri)
	into STRICT	nr_seq_item_seg_w
	from	agenda_int_area a,
			agenda_int_grupo b,
			agenda_int_grupo_item c,
			ageint_item_grupo_de_para d
	where	a.nr_sequencia			= b.nr_seq_area
	and		b.nr_sequencia			= c.nr_seq_grupo
	and		c.nr_sequencia			= d.nr_seq_item_pri
	and		d.nr_seq_item_seg		= nr_seq_item_grupo_p
	and		a.cd_estabelecimento	= cd_estab_destino_p;
end if;

if (nr_seq_item_pri_w	> 0) and (nr_seq_item_seg_w	> 0) then
	nr_seq_item_ret_p	:= 0;
elsif (nr_seq_item_pri_w	> 0) then
	nr_seq_item_ret_p	:= nr_seq_item_pri_w;
elsif (nr_seq_item_seg_w	> 0) then
	nr_seq_item_ret_p	:= nr_seq_item_seg_w;
else
	nr_seq_item_ret_p	:= 0;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
