CREATE OR REPLACE PROCEDURE ageint_obter_se_consiste_equip ( nr_seq_agenda_p bigint, cd_estabelecimento_p bigint, nr_seq_item_p bigint, nm_usuario_p text, dt_agenda_p timestamp, nr_duracao_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE

 
ds_erro_w		varchar(2000);
cd_equipamento_w	bigint;
nr_seq_classif_equip_w	bigint;
ds_erro_aux_w		varchar(2000);
ie_forma_equip_w	varchar(2);

C01 CURSOR FOR 
	SELECT	cd_equipamento, 
		nr_seq_classif_equip 
	from	ageint_equip_item 
	where	nr_seq_agenda_item	= nr_seq_item_p;		
		 

BEGIN 
 
select	coalesce(max(Obter_Valor_Param_Usuario(869, 23, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p)),'C') 
into STRICT	ie_forma_equip_w
;
 
open C01;
loop 
fetch C01 into	 
	cd_equipamento_w, 
	nr_seq_classif_equip_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	if (ie_forma_equip_w	= 'C') then 
		ds_erro_w := Ageint_obter_se_classif_disp(	nr_seq_agenda_p, nr_seq_classif_equip_w, ds_erro_w, nm_usuario_p, cd_estabelecimento_p, null, 'S', dt_agenda_p, nr_duracao_p);
	elsif (ie_forma_equip_w	= 'E') then 
		ds_erro_w := Ageint_obter_se_equip_disp(	nr_seq_agenda_p, cd_equipamento_w, ds_erro_w, nm_usuario_p, cd_Estabelecimento_p, null, 'S', dt_agenda_p, nr_duracao_p);
	end if;	
	if (ds_Erro_w IS NOT NULL AND ds_Erro_w::text <> '') then 
		ds_erro_aux_w	:= substr(ds_erro_w || ds_erro_aux_w,1, 2000);
	end if;
	end;
end loop;
close C01;
 
ds_Erro_p	:= substr(ds_erro_aux_w, 1, 255);
 
end;
$body$
