CREATE OR REPLACE PROCEDURE ageint_obter_se_cons_agenda ( cd_pessoa_Fisica_p text, dt_agenda_p timestamp, cd_estabelecimento_p bigint, nm_usuario_p text, ie_consulta_p INOUT text, ie_exame_p INOUT text, ie_quimio_p INOUT text) AS $body$
DECLARE


qt_agenda_w		bigint;
ie_exame_w		varchar(1)	:= 'N';
ie_consulta_w	varchar(1)	:= 'N';
ie_quimio_w		varchar(1)	:= 'N';


BEGIN

begin
	select	1
	into STRICT	qt_agenda_w
	from	agenda_paciente
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and		hr_inicio >= clock_timestamp()  LIMIT 1;
exception
when no_data_found then
	qt_agenda_w	:= 0;
end;

if (qt_agenda_w	> 0) then
	ie_exame_w	:= 'S';
end if;

begin
	select	1
	into STRICT	qt_agenda_w
	from	agenda_consulta
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and		dt_agenda >= clock_timestamp()  LIMIT 1;
exception
when no_data_found then
	qt_agenda_w	:= 0;
end;

if (qt_agenda_w	> 0) then
	ie_consulta_w	:= 'S';
end if;

begin
	select	1
	into STRICT	qt_agenda_w
	from	agenda_quimio
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and		dt_agenda >= clock_timestamp()  LIMIT 1;
exception
when no_data_found then
	qt_agenda_w	:= 0;
end;

if (qt_agenda_w	> 0) then
	ie_quimio_w	:= 'S';
end if;

ie_exame_p		:= ie_exame_w;
ie_consulta_p	:= ie_consulta_w;
ie_quimio_p		:= ie_quimio_w;

commit;

end;
$body$
