SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ageint_obter_se_equip_disp ( nr_seq_agenda_p bigint, cd_equipamento_p bigint, ds_erro_p INOUT text, nm_usuario_p text, cd_estabelecimento_p bigint, nr_seq_agenda_ant_p bigint, ie_novo_registro_p text, dt_inicial_p timestamp, nr_minuto_duracao_p bigint) AS $body$
DECLARE


dt_inicial_w		timestamp;
dt_final_w		timestamp;
qt_equip_man_w		bigint;
qt_equipamento_w		bigint;
qt_equip_agenda_w	bigint;
nr_seq_agenda_w		agenda_paciente.nr_sequencia%type;
ds_equipamento_w		varchar(80);
ds_agenda_w		varchar(50);
dt_agenda_w		timestamp;
nm_paciente_w		varchar(60);
nm_medico_w		varchar(50);
ie_consiste_w		varchar(15);
qt_novo_registro_w	integer;
cd_classif_equip_w	bigint;
qt_tempo_esterilizacao_w	bigint;
nr_minuto_duracao_w	bigint;

qt_equip_agenda_exec_w	bigint;

BEGIN

ie_consiste_w := Obter_Param_Usuario(871, 81, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_consiste_w);

qt_novo_registro_w	:= 0;
if (ie_novo_registro_p = 'S') then
	qt_novo_registro_w := 1;
end if;

qt_equip_man_w	:= obter_qt_equip_manutencao(null, cd_equipamento_p);

select	coalesce(sum(qt_equipamento) - qt_equip_man_w, 1),
	coalesce(max(cd_classificacao),0)
into STRICT	qt_equipamento_w,
	cd_classif_equip_w
from	equipamento
where	cd_equipamento	= cd_equipamento_p
and	ie_situacao	= 'A';

qt_tempo_esterilizacao_w	:= 0;
/* Obter o tempo de esterilizacao da classificacao do equipamento */

if (cd_classif_equip_w <> 0) then
	select	coalesce(max(qt_tempo_esterelizacao),0)
	into STRICT	qt_tempo_esterilizacao_w
	from	classif_equipamento
	where	nr_sequencia = cd_classif_equip_w;
end if;

dt_inicial_w	:= dt_inicial_p;
dt_final_w	:= (dt_inicial_w + ((nr_minuto_duracao_p + coalesce(qt_tempo_esterilizacao_w,0)) / 1440) - (1/86400));

/*select	hr_inicio,
	decode(ie_status_agenda, 'E', (hr_inicio + (qt_tempo_esterilizacao_w / 1440) - (1/86400)), (hr_inicio + ((nr_minuto_duracao + nvl(qt_tempo_esterilizacao_w,0)) / 1440) - (1/86400)))
into	dt_inicial_w,
	dt_final_w
from	agenda_paciente
where	nr_sequencia	= nr_seq_agenda_p;*/


/* Obter qtde de agendamentos que foram executadas no periodo - neste so e contado o tempo de esterilizacao e nao considera mais o tempo de duracao pois o mesmo ja foi realizado */

select	count(*)
into STRICT	qt_equip_agenda_exec_w
from	agenda_pac_equip a,
	agenda_paciente b
where	b.nr_sequencia	= a.nr_seq_agenda	
and	((hr_inicio between dt_inicial_w and dt_final_w) or
	(hr_inicio + (qt_tempo_esterilizacao_w / 1440) - (1/86400) between dt_inicial_w and dt_final_w) or
	((hr_inicio < dt_inicial_w) and (hr_inicio + (qt_tempo_esterilizacao_w / 1440) - (1/86400) > dt_final_w)))	
and	a.cd_equipamento = cd_equipamento_p
and	ie_status_agenda = 'E'
and	a.nr_seq_agenda <> coalesce(nr_seq_agenda_ant_p,0)
and	ie_origem_inf = 'I';

/* Obter qtde de agendamentos que nao foram executados e/ou cancelados no periodo */

select	count(*)
into STRICT	qt_equip_agenda_w
from	agenda_pac_equip a,
	agenda_paciente b
where	b.nr_sequencia	= a.nr_seq_agenda	
and	((hr_inicio between dt_inicial_w and dt_final_w) or
	(hr_inicio + ((nr_minuto_duracao + coalesce(qt_tempo_esterilizacao_w,0)) / 1440) - (1/86400) between dt_inicial_w and dt_final_w) or
	((hr_inicio < dt_inicial_w) and (hr_inicio + ((nr_minuto_duracao + coalesce(qt_tempo_esterilizacao_w,0)) / 1440) - (1/86400) > dt_final_w)))	
and	a.cd_equipamento = cd_equipamento_p
and	ie_status_agenda not in ('C','E')
and	a.nr_seq_agenda <> coalesce(nr_seq_agenda_ant_p,0)
and	ie_origem_inf = 'I';

qt_equip_agenda_w	:= qt_equip_agenda_exec_w + qt_equip_agenda_w + qt_novo_registro_w;

if (qt_equipamento_w < qt_equip_agenda_w) then

	select	max(nr_seq_agenda)
	into STRICT	nr_seq_agenda_w
	from	(SELECT	coalesce(max(b.nr_sequencia),0) nr_seq_agenda
		from	agenda_pac_equip a,
			agenda_paciente b
		where	b.nr_sequencia	= a.nr_seq_agenda	
		and	((hr_inicio between dt_inicial_w and dt_final_w) or
			(hr_inicio + (qt_tempo_esterilizacao_w / 1440) - (1/86400) between dt_inicial_w and dt_final_w) or
			((hr_inicio < dt_inicial_w) and (hr_inicio + (qt_tempo_esterilizacao_w / 1440) - (1/86400) > dt_final_w)))
		and	a.cd_equipamento = cd_equipamento_p
		and	ie_status_agenda = 'E'
		and	ie_origem_inf = 'I'	
		and	a.nr_seq_agenda <> coalesce(nr_seq_agenda_ant_p,0)
		
union
		
		SELECT	coalesce(max(b.nr_sequencia),0) nr_seq_agenda
		from	agenda_pac_equip a,
			agenda_paciente b
		where	b.nr_sequencia	= a.nr_seq_agenda	
		and	((hr_inicio between dt_inicial_w and dt_final_w) or  
			(hr_inicio + ((nr_minuto_duracao + coalesce(qt_tempo_esterilizacao_w,0)) / 1440) - (1/86400) between dt_inicial_w and dt_final_w) or
			((hr_inicio < dt_inicial_w) and (hr_inicio + ((nr_minuto_duracao + coalesce(qt_tempo_esterilizacao_w,0)) / 1440) - (1/86400) > dt_final_w)))		
		and	a.cd_equipamento = cd_equipamento_p
		and	ie_status_agenda not in ('C', 'E')
		and	a.nr_seq_agenda <> coalesce(nr_seq_agenda_ant_p,0)
		and	ie_origem_inf = 'I') alias33;
	
	if (nr_seq_agenda_w > 0) then
				
		select	substr(Obter_Desc_Equipamento(cd_equipamento_p),1,80),
			hr_inicio,
			substr(obter_nome_agenda(cd_agenda),1,50),
			nm_paciente,
			substr(obter_nome_pf(cd_medico),1,50),
			nr_minuto_duracao
		into STRICT	ds_equipamento_w,
			dt_agenda_w,
			ds_agenda_w,
			nm_paciente_w,
			nm_medico_w,
			nr_minuto_duracao_w
		from	agenda_paciente
		where	nr_sequencia	= nr_seq_agenda_w;
		
		if (qt_equip_man_w > 0) then
			ds_erro_p :=	substr(WHEB_MENSAGEM_PCK.get_texto(279053,'DS_CLASSIF_EQUI='||ds_equipamento_w||';DS_MENSAGEM='||' '),1,255);
		else
			ds_erro_p :=	substr(WHEB_MENSAGEM_PCK.get_texto(279055,'DS_CLASSIF_EQUI='|| ds_equipamento_w|| ';DT_AGENDA='|| to_char(dt_agenda_w,'dd/mm/yyyy hh24:mi')|| ';DS_AGENDA='|| ds_agenda_w|| ';QT_NR_TEMPO='||to_char(qt_tempo_esterilizacao_w + nr_minuto_duracao_w)) || chr(13),1,255);
			
		end if;

	elsif (qt_equip_man_w > 0) and (ie_novo_registro_p = 'S') then

		ds_erro_p := WHEB_MENSAGEM_PCK.get_texto(279053,'DS_CLASSIF_EQUI='||ds_equipamento_w||';DS_MENSAGEM='||' ');

	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ageint_obter_se_equip_disp ( nr_seq_agenda_p bigint, cd_equipamento_p bigint, ds_erro_p INOUT text, nm_usuario_p text, cd_estabelecimento_p bigint, nr_seq_agenda_ant_p bigint, ie_novo_registro_p text, dt_inicial_p timestamp, nr_minuto_duracao_p bigint) FROM PUBLIC;

