CREATE OR REPLACE PROCEDURE ageint_qt_marcar_dias_transf ( nr_sequencia_p bigint, dt_agenda_p timestamp, nm_usuario_p text, nr_seq_local_p bigint, cd_estabelecimento_p bigint, cd_pessoa_fisica_p text, nr_seq_item_p bigint, nr_seq_prof_p bigint default null, ds_retorno_p INOUT text  DEFAULT NULL) AS $body$
DECLARE


dt_agenda_w	timestamp;
ie_Gerar_w	varchar(1);
ds_retorno_w	varchar(4000);
ie_primeiro_w	varchar(1)	:= 'S';
ds_sep_w	varchar(3);
nr_minuto_duracao_w bigint;
nr_seq_item_w	bigint;
nr_duracao_w	bigint;

C01 CURSOR FOR
	SELECT	to_date(to_char(dt_prev_transf_quimio,'dd/mm/yyyy') || ' ' || to_char(dt_agenda_p,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss'),
		nr_minuto_duracao,
		nr_sequencia
	from	agenda_integrada_item
	where	nr_Seq_agenda_int	= nr_sequencia_p
	and	(dt_prev_transf_quimio IS NOT NULL AND dt_prev_transf_quimio::text <> '')
	order by 1;


BEGIN

nr_duracao_w := Obter_Param_Usuario(865, 3, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, nr_duracao_w);

open C01;
loop
fetch C01 into
	dt_agenda_w,
	nr_minuto_duracao_w,
	nr_seq_item_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (trunc(dt_agenda_w)	<> trunc(dt_agenda_p)) then
		CALL qt_gerar_hor_itens_ageint(
							cd_pessoa_Fisica_p,
							dt_agenda_w,
							nr_sequencia_p,
							coalesce(nr_duracao_w,15),
							nm_usuario_p,
							cd_estabelecimento_p,
							nr_seq_prof_p);
	end if;

	/*Qt_Gerar_Horario(
			null,
			null,
			dt_agenda_w,
			nm_usuario_p,
			nr_minuto_duracao_w,
			cd_pessoa_fisica_p,
			null,
			cd_estabelecimento_p,
			nr_seq_local_p,
			nr_seq_item_w,
			nr_seq_prof_p
			);*/
	ie_gerar_w := Qt_Atual_Dados_Transf_Ageint( nr_seq_item_w, dt_agenda_w, nm_usuario_p, nr_seq_local_p, cd_estabelecimento_p, nr_minuto_duracao_w, ie_gerar_w, nr_seq_prof_p);

	if (ie_Gerar_w	<> 'S') then
		if (ie_primeiro_w	= 'S') then
			ds_retorno_w	:= wheb_mensagem_pck.get_texto(791244);
			ie_primeiro_w	:= 'N';
		end if;
		ds_retorno_w	:= substr(ds_retorno_w || ds_sep_w || to_char(dt_agenda_w,'dd/mm/yyyy'),1,4000);
		ds_sep_w	:= ', ';
	end if;
	end;
end loop;
close C01;

ds_retorno_p	:= substr(ds_retorno_w,1,4000);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
