SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ageint_reserv_hor_agecons (nr_seq_agenda_p bigint, nm_usuario_p text, ie_reservado_p INOUT text) AS $body$
DECLARE


qt_agenda_w		bigint;
ie_reservado_w	varchar(1) := 'S';
qt_w			bigint;


BEGIN
/* acesso exclusivo tabela */


--lock table agenda_consulta in exclusive mode;
begin
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then
	select	1
	into STRICT	qt_w
	from	agenda_consulta
	where	nr_sequencia = nr_seq_agenda_p
	for update;

	select	coalesce(count(*),0)
	into STRICT	qt_agenda_w
	from	agenda_consulta
	where	nr_sequencia			= nr_seq_agenda_p
	and	(coalesce(ie_status_agenda,'L')	in ('L', 'LF')
	or	((ie_status_agenda = 'B')
	and (coalesce(dt_status::text, '') = '')));

	if (qt_agenda_w > 0) then
		update	agenda_consulta
		set	ie_status_agenda	= 'N',
			nm_usuario		= nm_usuario_p,
			nm_usuario_acesso	= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_sequencia		= nr_seq_agenda_p;
		commit;
		ie_reservado_w := 'S';
	else
		ie_reservado_w := 'N';
	end if;
end if;
exception
	when others then
	commit;
end;

commit;

ie_reservado_p := ie_reservado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
