CREATE OR REPLACE PROCEDURE ageint_valida_data_solic (NR_SEQ_AGEINT_ITEM_P bigint, DT_AGENDA_P timestamp, DS_ERRO_P INOUT text, IE_REGRA_P INOUT text) AS $body$
DECLARE

	
DS_ERRO_W varchar(2000) := '';
IE_REGRA_W varchar(1) := 'L';
DT_SOLICITACAO_W agenda_integrada_item.dt_solicitacao%type;
qt_dias_validade_w AGEINT_CONV_VALIDADE_SOLIC.qt_dias_validade%type;
qt_dias_dif_w bigint;
cd_convenio_w AGEINT_CONV_VALIDADE_SOLIC.cd_convenio%type;


BEGIN													

	select max(aii.dt_solicitacao)
		 , max(ai.cd_convenio)	 
	  into STRICT dt_solicitacao_w
		 , cd_convenio_w
	  from agenda_integrada_item aii
		 , agenda_integrada ai
	 where aii.nr_seq_agenda_int = ai.nr_sequencia
	   and aii.nr_sequencia = nr_seq_ageint_item_p;
	
	select coalesce(max(cd_convenio),cd_convenio_w)
	  into STRICT cd_convenio_w
      from AGENDA_INTEGRADA_CONV_ITEM
     where nr_seq_agenda_item	= nr_seq_ageint_item_p;
	
	if (dt_solicitacao_w IS NOT NULL AND dt_solicitacao_w::text <> '') then	 
		select coalesce(max(ie_regra),'L')
			 , max(qt_dias_validade)
		  into STRICT ie_regra_w
			 , qt_dias_validade_w
		  from AGEINT_CONV_VALIDADE_SOLIC acvs
		 where coalesce(acvs.cd_convenio,cd_convenio_w) = cd_convenio_w
		   and coalesce(acvs.ie_situacao,'A') = 'A';
		
		qt_dias_dif_w := trunc(dt_agenda_p) - dt_solicitacao_w;

		if (qt_dias_dif_w > qt_dias_validade_w) and (coalesce(qt_dias_validade_w,0) > 0) then
			ds_erro_p := wheb_mensagem_pck.get_texto(1180221);
			ie_regra_p := IE_REGRA_W;
		else
			ie_regra_p := 'L';
		end if;
	end if;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
