CREATE OR REPLACE PROCEDURE ageint_verifica_med_rodizio (nr_seq_ageint_item_p bigint, dt_agenda_p timestamp, cd_estabelecimento_p bigint, nm_usuario_p text, cd_pessoa_fisica_p text, qt_dias_rodizio_p bigint, ie_permite_p INOUT text) AS $body$
DECLARE

 
cd_medico_w		varchar(10);	
nr_seq_ageint_lib_w	bigint;	
ds_erro_w		varchar(255);	
cd_agenda_w		bigint;
ie_turno_medico_w	varchar(1);
nr_seq_medico_regra_w	bigint;
ie_permite_w		varchar(1);
					
C01 CURSOR FOR 
	SELECT 	--nr_sequencia, 
		distinct cd_pessoa_fisica, 
		cd_agenda, 
		nr_seq_medico_regra 
	from	ageint_lib_usuario 
	where	nr_seq_ageint_item 	= nr_seq_ageint_item_p 
	and	cd_pessoa_fisica	= cd_pessoa_fisica_p;	
					 

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	--nr_seq_ageint_lib_w, 
	cd_medico_w, 
	cd_agenda_w, 
	nr_seq_medico_regra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	ie_turno_medico_w := Ageint_Obter_Se_Turno_Med_Rod(cd_medico_w, dt_agenda_p, cd_agenda_w, nm_usuario_p, nr_seq_medico_regra_w, cd_estabelecimento_p);
	if (ie_turno_medico_w = 'S') then 
		ds_erro_w := Ageint_consistir_rozidio_exame(nr_seq_ageint_item_p, cd_medico_w, dt_agenda_p, cd_agenda_w, qt_dias_rodizio_p, cd_estabelecimento_p, nm_usuario_p, ds_erro_w);
	 
		if (coalesce(ds_erro_w::text, '') = '') then 
			ie_permite_w	:= 'S';
		else 
			ie_permite_w	:= 'N';
		end if;
	else 
		ie_permite_w	:= 'S';
	end if;
	end;
end loop;
close C01;
 
ie_permite_p	:= ie_permite_w;
 
commit;
 
end;
$body$
