SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE agendamento_externo_cancelado (nr_seq_lista_espera_p bigint, cd_motivo_canc_p text, nm_usuario_p text) AS $body$
DECLARE




nr_seq_regulacao_atend_w		regulacao_atend.nr_sequencia%type;

nr_seq_situacao_atual_w			lista_espera_situacao_cont.nr_sequencia%type;

qt_motivo_w						bigint;

cd_expressao_reg_w				bigint;

cd_expressao_his_w				bigint;

ie_status_w						varchar(5);




BEGIN



	if (nr_seq_lista_espera_p IS NOT NULL AND nr_seq_lista_espera_p::text <> '') then

		begin



			select	count(1)

			into STRICT	qt_motivo_w

			from	agenda_motivo_cancelamento a,

					regra_motivo_realizado_ext b

			where	a.nr_seq_grupo	= b.nr_seq_grupo_motivo_cancel

			and		a.cd_motivo		= cd_motivo_canc_p;



			update	agenda_lista_espera

			set		ie_status_espera	= 'C',

					dt_atualizacao		= clock_timestamp(),

					nm_usuario			= nm_usuario_p

			where	nr_sequencia		= nr_seq_lista_espera_p;



			if (qt_motivo_w > 0) then

				begin

					cd_expressao_his_w	:= 1100153;

					cd_expressao_reg_w	:= 1100153;

					ie_status_w			:= 'FI';

					

					select	max(nr_sequencia)

					into STRICT	nr_seq_situacao_atual_w

					from	lista_espera_situacao_cont

					where	ie_situacao = 'A'

					and		ie_tipo_contato = 'PJ';

				end;

			else

				begin

					cd_expressao_his_w	:= 1104121;

					cd_expressao_reg_w	:= 1100152;

					ie_status_w			:= 'CA';



					select	max(nr_sequencia)

					into STRICT	nr_seq_situacao_atual_w

					from	lista_espera_situacao_cont

					where	ie_situacao = 'A'

					and		ie_tipo_contato = 'PD';

				end;

			end if;



			select	max(nr_seq_regulacao)

			into STRICT	nr_seq_regulacao_atend_w

			from	agenda_lista_espera a

			where	nr_sequencia = nr_seq_lista_espera_p;



			if (nr_seq_regulacao_atend_w IS NOT NULL AND nr_seq_regulacao_atend_w::text <> '') then

				CALL alterar_status_regulacao(nr_seq_regulacao_atend_w, ie_status_w, obter_expressao_dic_objeto(cd_expressao_reg_w), obter_pessoa_fisica_usuario(nm_usuario_p, null));

			end if;



			if (nr_seq_situacao_atual_w IS NOT NULL AND nr_seq_situacao_atual_w::text <> '') then

				insert into ag_lista_espera_contato(

					nr_sequencia,

					dt_atualizacao,

					dt_atualizacao_nrec,

					nm_usuario,

					nm_usuario_nrec,

					nr_seq_lista_espera,

					dt_contato,

					ie_origem,

					nm_pessoa_contato,

					nr_seq_situacao_atual,

					ds_observacao,

					dt_liberacao,

					nm_usuario_liberacao,

					ie_contato_realizado

				) values (

					nextval('ag_lista_espera_contato_seq'),

					clock_timestamp(),

					clock_timestamp(),

					nm_usuario_p,

					nm_usuario_p,

					nr_seq_lista_espera_p,

					clock_timestamp(),

					'A',

					obter_pessoa_fisica_usuario(nm_usuario_p, null),

					nr_seq_situacao_atual_w,

					obter_expressao_dic_objeto(cd_expressao_his_w),

					clock_timestamp(),

					nm_usuario_p,

					'N'

				);

			end if;

		end;

	end if;

end;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
