SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE agendar_paciente_gercon ( nr_seq_lote_p bigint, nr_seq_gercon_p bigint default null, nr_seq_agenda_p bigint default null) AS $body$
DECLARE


C01 CURSOR FOR
	SELECT	nr_Sequencia,
			cd_profissional,
			cd_especialidade,
			dt_agenda,
			CD_PESSOA_FISICA
	from	GERCON_LOTE_IMP_AGENDA
	where	NR_SEQ_LOTE = nr_seq_lote_p
	and		coalesce(NR_SEQ_AGENDAMENTO_CONS::text, '') = ''
	order by DT_AGENDA,
			 CD_ESPECIALIDADE,
			 CD_PROFISSIONAL,
			 NR_SEQ_AGENDAMENTO_CONS;

c01_w				c01%rowtype;
cd_agenda_w			agenda.cd_agenda%type;
nr_Sequencia_w		agenda_consulta.nr_sequencia%type;
cd_pessoa_fisica_w	gercon_lote_imp_agenda.cd_pessoa_fisica%type;


BEGIN

if (nr_seq_lote_p IS NOT NULL AND nr_seq_lote_p::text <> '') then
	open C01;
	loop
	fetch C01 into
		c01_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
			if (coalesce(c01_w.cd_profissional,0) <> 0) then

				select	max(cd_agenda)
				into STRICT	cd_agenda_w
				from	agenda
				where 	CD_tipo_agenda in (3, 4)
				and		CD_ESPECIALIDADE = obter_conversao_externa_int(null, 'AGENDA','CD_ESPECIALIDADE', c01_w.cd_Especialidade, 'GERCON')
				and		CD_PESSOA_FISICA = c01_w.cd_profissional;

				select	nr_sequencia
				into STRICT	nr_sequencia_w
				from ( 	SELECT	nr_sequencia
							from 	agenda_consulta
							where	cd_agenda = cd_agenda_w
							and		trunc(dt_agenda) = trunc(to_date(c01_w.dt_agenda))
							and		IE_STATUS_AGENDA =	'L'
							order 	by dt_agenda,
									   nr_seq_hora) alias3 LIMIT 1;

				update	agenda_consulta
				set		ie_status_agenda = 'N',
						cd_pessoa_fisica = c01_w.cd_pessoa_fisica,
						NM_USUARIO_ORIGEM = wheb_usuario_pck.get_nm_usuario,
						dt_agendamento = clock_timestamp()
				where	nr_sequencia = nr_Sequencia_w;

				update	gercon_lote_imp_agenda
				set		NR_SEQ_AGENDAMENTO_CONS = nr_sequencia_w,
						dt_atualizacao = clock_timestamp()
				where	nr_sequencia = c01_w.nr_sequencia;

			end if;
		end;
	end loop;
	close C01;
else
	select	cd_pessoa_fisica
	into STRICT	cd_pessoa_fisica_w
	from	gercon_lote_imp_agenda
	where	nr_sequencia = nr_seq_gercon_p;

	update	agenda_consulta
	set		ie_status_agenda = 'N',
			cd_pessoa_fisica = cd_pessoa_fisica_w,
			NM_USUARIO_ORIGEM = wheb_usuario_pck.get_nm_usuario,
			dt_agendamento = clock_timestamp()
	where	nr_sequencia = nr_seq_agenda_p;

	update	gercon_lote_imp_agenda
	set		NR_SEQ_AGENDAMENTO_CONS = nr_seq_agenda_p,
			dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_seq_gercon_p;


end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE agendar_paciente_gercon ( nr_seq_lote_p bigint, nr_seq_gercon_p bigint default null, nr_seq_agenda_p bigint default null) FROM PUBLIC;

