CREATE OR REPLACE PROCEDURE agenda_paciente_atribdblclick2 ( cd_Agenda_p bigint, dt_agenda_p timestamp, cd_pessoa_fisica_p text, cd_convenio_p bigint, cd_categoria_p text, cd_usuario_convenio_p text, cd_estabelecimento_p bigint, cd_perfil_p bigint, nm_usuario_p text, cd_plano_padrao_p INOUT text, cd_plano_cod_usuario_p INOUT text, ds_retorno_p INOUT text, ie_classif_retorno_p INOUT text, ie_se_pac_agenda_p INOUT text, ie_bloq_agen_p INOUT text) AS $body$
DECLARE


cd_plano_padrao_w		varchar(10);
cd_plano_cod_usuario_w		varchar(10);
ie_consiste_agenda_retorno_w	varchar(255);
ds_retorno_w			varchar(4000)	:= '';
ie_classif_retorno_w		varchar(5);
ie_se_pac_agenda_w		varchar(1);
nr_atendimento_w		bigint;
nr_seq_regra_msg_w		bigint;
ds_msg_regra_w			varchar(255);
ie_bloq_agen_w			varchar(255);
		

BEGIN

if (cd_convenio_p > 0) then
	begin
	
	select	max(cd_plano_padrao)
	into STRICT	cd_plano_padrao_w
	from	categoria_convenio
	where 	cd_convenio	= cd_convenio_p
	and 	cd_categoria	= cd_categoria_p;
		
	cd_plano_cod_usuario_w	:= substr(obter_plano_cod_usuario_conv(cd_convenio_p, cd_usuario_convenio_p),1,10);
	
	end;
end if;

ie_consiste_agenda_retorno_w := Obter_Param_Usuario(820, 55, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p, ie_consiste_agenda_retorno_w);

if (cd_Agenda_p > 0) and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (ie_consiste_agenda_retorno_w <> 'N') then
	SELECT * FROM identifica_agenda_retorno(	dt_agenda_p, cd_pessoa_fisica_p, cd_convenio_p, nm_usuario_p, cd_Agenda_p, null, ds_retorno_w, ie_classif_retorno_w, nr_atendimento_w, nr_seq_regra_msg_w, ds_msg_regra_w, ie_bloq_agen_w, null) INTO STRICT ds_retorno_w, ie_classif_retorno_w, nr_atendimento_w, nr_seq_regra_msg_w, ds_msg_regra_w, ie_bloq_agen_w;
end if;

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	ie_se_pac_agenda_w	:= substr(Obter_Se_Pac_Agenda(cd_pessoa_fisica_p, nm_usuario_p, cd_estabelecimento_p),1,1);
end if;

cd_plano_padrao_p	:= cd_plano_padrao_w;
cd_plano_cod_usuario_p	:= cd_plano_cod_usuario_w;
ds_retorno_p		:= ds_retorno_w;
ie_classif_retorno_p	:= ie_classif_retorno_w;
ie_se_pac_agenda_p	:= ie_se_pac_agenda_w;
ie_bloq_agen_p		:= ie_bloq_agen_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
