CREATE OR REPLACE PROCEDURE agenda_pac_equip_beforepost ( ie_equipamento_lanc_p text, ie_const_equip_p text, ie_forma_cad_equip_p text, nr_seq_agenda_p bigint, cd_equipamento_p bigint, nr_seq_classif_equip_p bigint, ie_novo_p text, cd_estabelecimento_p bigint, ds_erro_p INOUT text, ie_equip_out_p INOUT text, nm_usuario_p text) AS $body$
DECLARE

 
ie_existe_agenda_w	varchar(1);
ds_erro_w		varchar(2000);
ie_status_equipamento_w	varchar(15);

BEGIN
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
	 
	if (ie_equipamento_lanc_p = 'S') then 
		begin 
		select	CASE WHEN coalesce(count(*),0)=0 THEN 'N'  ELSE 'S' END  
		into STRICT	ie_existe_agenda_w 
		from	agenda_pac_equip 
		where	ie_origem_inf = 'I' 
		and	nr_seq_agenda = nr_seq_agenda_p 
		and	cd_equipamento= cd_equipamento_p;
		 
		if (ie_existe_agenda_w = 'S') then 
			begin 
			CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(73729);
			 
			end;
		end if;
		end;
	end if;
	 
	if (ie_const_equip_p <> 'N') then 
		begin 
		if (ie_forma_cad_equip_p = 'C') then 
			begin 
			ds_erro_w := obter_se_classif_equip_disp( 
				nr_seq_agenda_p, nr_seq_classif_equip_p, ds_erro_w, nm_usuario_p, cd_estabelecimento_p, '', ie_novo_p);
			end;
		elsif (ie_forma_cad_equip_p = 'E') then 
			begin 
			ds_erro_w := obter_se_equip_disp_cirur( 
				nr_seq_agenda_p, cd_equipamento_p, ds_erro_w, nm_usuario_p, cd_estabelecimento_p, '', ie_novo_p);
				 
			ie_status_equipamento_w := obter_status_equipamento( 
				nr_seq_agenda_p, 
				cd_equipamento_p, 
				'S');
			end;
		end if;
		end;
	end if;
	end;
end if;
ds_erro_p := ds_erro_w;
ie_equip_out_p := ie_status_equipamento_w;
commit;
end;
$body$
