SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE agequi_atualizar_dt_real ( nr_seq_pend_agenda_p bigint, dt_agenda_real_p timestamp, nm_usuario_p text) AS $body$
DECLARE


dt_menor_agenda_w	timestamp;
nr_sequencia_w		bigint;
qt_dif_entre_data_w	bigint;
C01 CURSOR FOR
	SELECT	b.nr_sequencia
	from	agenda_quimio a,
		w_gerar_consulta_quimio b
	where	a.nr_seq_pend_agenda		= b.nr_seq_pend_agenda
	and	b.nr_seq_pend_agenda 		= nr_seq_pend_agenda_p
	and	coalesce(b.ie_gerar_consulta,'N')	= 'S'
	order by 1;


BEGIN

if (coalesce(nr_seq_pend_agenda_p,0) > 0) and (dt_agenda_real_p IS NOT NULL AND dt_agenda_real_p::text <> '') then

	select	min(coalesce(b.dt_agenda_real, b.dt_agenda))
	into STRICT	dt_menor_agenda_w
	from	agenda_quimio a,
		w_gerar_consulta_quimio b
	where	a.nr_seq_pend_agenda		= b.nr_seq_pend_agenda
	and	b.nr_seq_pend_agenda 		= nr_seq_pend_agenda_p
	and	coalesce(b.ie_gerar_consulta,'N')	= 'S';


	if (dt_menor_agenda_w IS NOT NULL AND dt_menor_agenda_w::text <> '') then

	qt_dif_entre_data_w := OBTER_DIAS_ENTRE_DATAS(dt_menor_agenda_w,dt_agenda_real_p);

	open C01;
	loop
	fetch C01 into
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		update	w_gerar_consulta_quimio
		set	dt_agenda_real = (dt_agenda + qt_dif_entre_data_w)
		where	nr_sequencia   = nr_sequencia_w;

		end;
	end loop;
	close C01;

	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
