CREATE OR REPLACE PROCEDURE ageserv_consiste_falta_pac ( cd_pessoa_fisica_p text, dt_agenda_p timestamp, cd_agenda_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ds_Retorno_p INOUT text) AS $body$
DECLARE


nr_seq_falta_w	bigint;
dt_agenda_w		timestamp;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_falta_w
from	agenda_consulta
where	ie_status_agenda	in ('F','I')
and		nr_sequencia	= (	SELECT	max(nr_sequencia)
							from	agenda_consulta
							where	cd_pessoa_fisica	= cd_pessoa_fisica_p
							and		dt_agenda			< dt_agenda_p
							and		ie_status_agenda	<> 'C');

if (nr_seq_falta_w	> 0) then
	select	max(dt_agenda)
	into STRICT	dt_agenda_w
	from	agenda_consulta
	where	nr_sequencia	= nr_seq_falta_w;

	ds_Retorno_p	:= wheb_mensagem_pck.get_texto(791677,
						'dt_agendamento='|| to_char(dt_agenda_w,pkg_date_formaters.localize_mask('timestamp', pkg_date_formaters.getUserLanguageTag(cd_estabelecimento_p, nm_usuario_p))));
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
