SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ageserv_gerar_agenda_cirurgia ( nr_seq_agenda_p bigint, nr_seq_cir_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_pessoa_fisica_w	varchar(10);	
cd_procedimento_w	bigint;
nr_seq_proc_interno_w	bigint;
ds_observacao_w		varchar(4000);
cd_convenio_w		integer;
ie_origem_proced_w	bigint;
nm_paciente_w		varchar(60);
cd_usuario_convenio_w	varchar(30);
cd_plano_w		varchar(10);
cd_procedencia_w	integer;
cd_categoria_w		varchar(10);
nr_doc_convenio_w	varchar(20);
dt_validade_carteira_w	timestamp;
cd_doenca_cid_w		varchar(10);
nr_seq_proced_w		agenda_paciente_proc.nr_seq_agenda%type;
cd_medico_w		varchar(10);

c01 CURSOR FOR
	SELECT	cd_procedimento,
		ie_origem_proced,
		nr_seq_proc_interno
	from	agenda_consulta_proc
	where	nr_seq_agenda = nr_seq_agenda_p
	order by
		nr_seq_agenda;

BEGIN
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') and (nr_seq_cir_p IS NOT NULL AND nr_seq_cir_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	select		b.cd_pessoa_fisica,
			b.cd_procedimento,
			b.nr_seq_proc_interno,
			b.ds_observacao,
			b.cd_convenio,
			b.ie_origem_proced,
			b.nm_paciente,
			b.cd_usuario_convenio,
			b.cd_plano,
			b.cd_procedencia,
			b.cd_categoria,
			b.nr_doc_convenio,
			b.dt_validade_carteira,
			b.cd_cid,
			b.cd_medico
	into STRICT		cd_pessoa_fisica_w,
			cd_procedimento_w,
			nr_seq_proc_interno_w,
			ds_observacao_w,
			cd_convenio_w,
			ie_origem_proced_w,
			nm_paciente_w,
			cd_usuario_convenio_w,
			cd_plano_w,
			cd_procedencia_w,
			cd_categoria_w,
			nr_doc_convenio_w,
			dt_validade_carteira_w,
			cd_doenca_cid_w,
			cd_medico_w
	from	agenda a,
		agenda_consulta b
	where	b.nr_sequencia	= nr_seq_agenda_p
	and	a.cd_agenda	= b.cd_agenda;

	update	agenda_paciente
	set	cd_pessoa_fisica		= cd_pessoa_fisica_w,
		nm_usuario			= nm_usuario_p,
		dt_atualizacao			= clock_timestamp(),
		cd_procedimento			= cd_procedimento_w,
		nr_seq_proc_interno		= nr_seq_proc_interno_w,
		ds_observacao			= ds_observacao_w,
		cd_convenio			= cd_convenio_w,
		ie_origem_proced		= ie_origem_proced_w,
		nm_paciente			= nm_paciente_w,
		cd_usuario_convenio		= cd_usuario_convenio_w,
		cd_plano			= cd_plano_w,
		cd_procedencia			= cd_procedencia_w,
		cd_categoria			= cd_categoria_w,
		nr_doc_convenio			= nr_doc_convenio_w,
		dt_validade_carteira		= dt_validade_carteira_w,
		cd_doenca_cid			= cd_doenca_cid_w,
		ie_status_agenda		= 'N',
		cd_medico			= cd_medico_w
	where	nr_sequencia			= nr_seq_cir_p;

	commit;

	open c01;
		loop
		fetch c01 into	cd_procedimento_w,
				ie_origem_proced_w,
				nr_seq_proc_interno_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin
			select	coalesce(max(nr_seq_agenda),0)+1
			into STRICT	nr_seq_proced_w
			from	agenda_paciente_proc
			where	nr_sequencia = nr_seq_cir_p;
			
			insert into agenda_paciente_proc(
					nr_sequencia,
					nr_seq_agenda,
					cd_procedimento,
					ie_origem_proced,
					nr_seq_proc_interno,
					dt_atualizacao,
					nm_usuario)
				values (
					nr_seq_cir_p,
					nr_seq_proced_w,
					cd_procedimento_w,
					ie_origem_proced_w,
					nr_seq_proc_interno_w,
					clock_timestamp(),
					nm_usuario_p);
			end;
		end loop;
		close c01;
end if;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ageserv_gerar_agenda_cirurgia ( nr_seq_agenda_p bigint, nr_seq_cir_p bigint, nm_usuario_p text) FROM PUBLIC;

