CREATE OR REPLACE PROCEDURE ageweb_gerar_pesquisa (cd_especialidade_p bigint, cd_usuario_p text, cd_convenio_p bigint, cd_categoria_p text, cd_plano_p text, cd_pessoa_fisica_p text, cd_estab_p bigint) AS $body$
DECLARE

 
cd_agenda_w		bigint;
ds_agenda_w		varchar(255);
ds_especialidade_w	varchar(255);
dt_agenda_w		timestamp;
				
C01 CURSOR FOR 
	SELECT	a.cd_agenda, 
		substr(obter_nome_pf(b.cd_pessoa_fisica),1,255), 
		substr(c.ds_especialidade||' '||a.ds_complemento,1,255) 
	from	agenda a, 
		pessoa_fisica b, 
		especialidade_medica c 
	where 	a.cd_pessoa_fisica = b.cd_pessoa_fisica 
	and	a.cd_especialidade = c.cd_especialidade 
	and	a.cd_tipo_agenda = 3 
	and	coalesce(a.ie_agenda_web,'S') = 'S' 
	and	coalesce(a.ie_situacao,'A') = 'A' 
	and	ageweb_se_agenda_cons_web(a.cd_agenda, cd_usuario_p, cd_convenio_p, cd_categoria_p, cd_plano_p, 'N', null, cd_especialidade_p) = 'S' 
	and	((c.cd_especialidade = cd_especialidade_p) or (coalesce(cd_especialidade_p::text, '') = '')) 
	and	((a.cd_pessoa_fisica = cd_pessoa_fisica_p) or (coalesce(cd_pessoa_fisica_p::text, '') = '')) 
	and	((a.cd_estabelecimento = cd_estab_p) or (coalesce(cd_estab_p::text, '') = ''));
				

BEGIN 
 
delete	FROM ageweb_pesquisa_agenda 
where	cd_pessoa_fisica = cd_usuario_p;
 
open C01;
loop 
fetch C01 into	 
	cd_agenda_w, 
	ds_agenda_w, 
	ds_especialidade_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	CALL Ageweb_gerar_horarios( 
			cd_agenda_w, 
			cd_usuario_p, 
			null, 
			'S');
			 
	select	min(dt_agenda) 
	into STRICT	dt_agenda_w 
	from	ageweb_horarios 
	where	cd_agenda = cd_agenda_w 
	and	cd_pessoa_fisica = cd_usuario_p;
 
	if (dt_agenda_w IS NOT NULL AND dt_agenda_w::text <> '') then	 
		insert into ageweb_pesquisa_agenda(	 
			cd_agenda, 
			cd_pessoa_fisica, 
			ds_agenda, 
			dt_agenda, 
			ds_especialidade, 
			dt_atualizacao, 
			nm_usuario) 
		values (	cd_agenda_w, 
			cd_usuario_p, 
			ds_agenda_w, 
			dt_agenda_w, 
			ds_especialidade_w, 
			clock_timestamp(), 
			cd_usuario_p);
	end if;
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ageweb_gerar_pesquisa (cd_especialidade_p bigint, cd_usuario_p text, cd_convenio_p bigint, cd_categoria_p text, cd_plano_p text, cd_pessoa_fisica_p text, cd_estab_p bigint) FROM PUBLIC;

