CREATE OR REPLACE PROCEDURE agfa_gerar_agenda_integrada (cd_agenda_externa_p bigint, dt_agenda_p timestamp, cd_pessoa_fisica_p text, cd_convenio_p bigint, cd_categoria_p text, cd_plano_p text, cd_usuario_convenio_p text, nr_seq_proc_interno_p text, nr_seq_integrada_p INOUT bigint, nr_seq_item_p INOUT bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_integracao_w		bigint;
nr_seq_item_w			bigint;
dt_inicio_dia_w			timestamp;
dt_fim_dia_w			timestamp;
nr_seq_status_w			bigint;


BEGIN

dt_inicio_dia_w			:= trunc(dt_agenda_p, 'dd');
dt_fim_dia_w			:= trunc(dt_agenda_p, 'dd') - 86400/86399;


if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '') then
	begin

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_integracao_w
	from	agenda_integrada
	where	cd_agenda_externa	= cd_agenda_externa_p
	and	cd_pessoa_fisica	= cd_pessoa_fisica_p
	/*and	DT_INICIO_AGENDAMENTO	between dt_inicio_dia_w and dt_fim_dia_w;*/

	and	trunc(dt_inicio_agendamento,'dd') = trunc(dt_agenda_p,'dd');

	if (nr_seq_integracao_w = 0) then

		select	nextval('agenda_integrada_seq')
		into STRICT	nr_seq_integracao_w
		;

		select	min(nr_sequencia)
		into STRICT	nr_seq_status_w
		from	agenda_integrada_status;

		insert 	into agenda_integrada(nr_sequencia          ,
			dt_atualizacao         ,
			nm_usuario             ,
			dt_atualizacao_nrec    ,
			nm_usuario_nrec        ,
			dt_inicio_agendamento  ,
			dt_fim_agendamento     ,
			nr_seq_status          ,
			cd_pessoa_fisica       ,
			cd_convenio            ,
			cd_categoria           ,
			cd_plano               ,
			nr_doc_convenio        ,
			dt_validade_carteira   ,
			cd_usuario_convenio    ,
			nm_contato             ,
			nr_telefone            ,
			cd_profissional        ,
			cd_estabelecimento     ,
			cd_agenda_externa)
		values (nr_seq_integracao_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			dt_agenda_p,
			null,
			nr_seq_status_w,
			cd_pessoa_fisica_p,
			cd_convenio_p,
			cd_categoria_p,
			cd_plano_p,
			null,
			null,
			cd_usuario_convenio_p,
			null,
			null,
			null,
			cd_estabelecimento_p,
			cd_agenda_externa_p);

	end if;

	select	nextval('agenda_integrada_item_seq')
	into STRICT	nr_seq_item_w
	;

	insert 	into agenda_integrada_item(nr_sequencia       ,
		nr_seq_agenda_int   ,
		dt_atualizacao      ,
		nm_usuario          ,
		dt_atualizacao_nrec ,
		nm_usuario_nrec     ,
		nr_seq_proc_interno ,
		ie_tipo_agendamento ,
		cd_medico           ,
		cd_especialidade    ,
		vl_item             ,
		ie_regra            ,
		nr_seq_agenda_cons  ,
		nr_seq_agenda_exame )
	values (nr_seq_item_w,
		nr_seq_integracao_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_proc_interno_p,
		'E',
		null,
		null,
		null,
		null,
		null,
		null);

	end;
end if;

nr_seq_integrada_p	:= nr_seq_integracao_w;
nr_seq_item_p		:= nr_seq_item_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
