CREATE OR REPLACE PROCEDURE aghos_ajusta_leito ( nr_seq_solicitacao_p bigint, cd_cod_posto_enf_p bigint, cod_enfermaria_p bigint, cod_leito_p bigint, cod_digito_leito_p text, ie_reserva_leito_w text default 'N') AS $body$
DECLARE


cd_setor_destino_w	bigint;
nr_seq_interno_w	bigint;
cd_pessoa_fisica_w	varchar(10);
ie_status_unidade_w	varchar(2);
nr_internacao_w		bigint;


BEGIN

select	max(nr_seq_interno),
	max(ie_status_unidade)
into STRICT	nr_seq_interno_w,
	ie_status_unidade_w
from	unidade_atendimento
where	cd_setor_atendimento in (SELECT	b.cd_setor_atendimento
				from	setor_atendimento b
				where	b.cd_setor_externo = cd_cod_posto_enf_p)
and	nm_setor_integracao = cod_enfermaria_p || ';' || cod_leito_p || ';' || trim(both cod_digito_leito_p);

update	solicitacao_tasy_aghos
set	nr_seq_unidade  = nr_seq_interno_w
where	nr_sequencia = nr_seq_solicitacao_p;

select	max(cd_pessoa_fisica),
	max(nr_internacao)
into STRICT	cd_pessoa_fisica_w,
	nr_internacao_w
from	solicitacao_tasy_aghos
where	nr_sequencia = nr_seq_solicitacao_p;

begin
	if (ie_reserva_leito_w = 'S') and (ie_status_unidade_w = 'L') then
		update	unidade_atendimento
		set	ie_status_unidade = 'R',
			cd_paciente_reserva = cd_pessoa_fisica_w,
			nm_usuario_reserva = 'Aghos',
			nr_internacao_aghos = nr_internacao_w
		where	nr_seq_interno = nr_seq_interno_w;
	end if;
exception
	when others then
		null;
end;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
