CREATE OR REPLACE PROCEDURE aghos_enviar_evolucao_atx (nr_internacao_p bigint, nr_seq_evolucao_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ds_motivo_cancelamento_w	varchar(1000);
cd_cnes_solicitante_w		varchar(1000);
ds_sep_bv_w			varchar(50);
ds_comando_w			varchar(2000);
ie_tipo_evolucao_w		varchar(2);
ds_evolucao_w			varchar(2000);
ds_usuario_w			varchar(15);
BEGIN
 
/*  
IDF_GSH_I_INTERN_CANCEL_ENT 	NUMBER 		not null 
TIP_STATUS 			VARCHAR2 1 	not null 
DES_ERRO 			VARCHAR2 500 	null 
DAT_INTEGRACAO 		DATE 		not null 
DES_USUARIO 			VARCHAR2 15 	not null 
COD_CNES_SOLICITANTE 		NUMBER 7 	not null 
IDF_INTERNACAO 		NUMBER 9 	not null 
DES_MOTIVO_CANCELAMENTO 	VARCHAR2 1000 	not null 
*/
 
 
select	max(substr(obter_cnes_estab(a.cd_estabelecimento), 1, 7)), 
	coalesce(max(substr(obter_dados_param_atend(a.cd_estabelecimento, 'US'), 1, 15)), 'TREINA') nm_usuario 
into STRICT	cd_cnes_solicitante_w, 
	ds_usuario_w 
from	atendimento_paciente a, 
	solicitacao_tasy_aghos b 
where	((b.nr_atend_original = a.nr_atendimento) or (b.nr_atendimento = a.nr_atendimento)) 
and	b.nr_internacao = nr_internacao_p;
 
select	max(ie_tipo_evolucao), 
	max(ds_evolucao) 
into STRICT	ie_tipo_evolucao_w, 
	ds_evolucao_w 
from	evolucao_tasy_aghos 
where	nr_internacao = coalesce(nr_internacao_p, 0) 
and	nr_sequencia = coalesce(nr_seq_evolucao_p, 0);
 
ds_sep_bv_w := obter_separador_bv;
 
ds_comando_w :=	'declare '|| 
		'	evoluc_sai gsh_i_pkg_integra_sai.evoluc_sai@tasy_aghos; '|| 
		'	ds_erro_ww	varchar2(500); '|| 
		'begin '|| 
		' '|| 
		'  begin ' || 
		'	gsh_i_prc_evoluc_ent@tasy_aghos( 0, '|| 
		'					''A'', '|| 
		'					ds_erro_ww, '|| 
		'					sysdate, '|| 
		'					:ds_usuario_p, '|| 
		'					:cd_cnes_solicitante_p, '|| 
		'					:nr_internacao_p, '|| 
		'					:ie_tipo_evolucao_p, ' || 
		'					:ds_evolucao_p); ' || 
		' '|| 
		'	gsh_i_pkg_integra_sai.gsh_i_prc_evoluc_sai@tasy_aghos(evoluc_sai); '|| 
		' '|| 
		'	if	(evoluc_sai.first is not null) then '|| 
		'		for i in evoluc_sai.first..evoluc_sai.last loop '|| 
		' '|| 
		'			if	(evoluc_sai(i).idf_internacao = :nr_internacao_p) then '|| 
		' '|| 
		'				ds_erro_ww := null; '||	 
		'				gsh_i_prc_evoluc_s_u@tasy_aghos(	evoluc_sai(i).idf_gsh_i_evoluc_sai, '|| 
		'									''P'', '|| 
		'									ds_erro_ww); '||			 
		'			end if; '|| 
		'		end loop; '|| 
		'	end if; '|| 
		' commit; '||		 
		' DBMS_SESSION.CLOSE_DATABASE_LINK(''tasy_aghos''); ' ||			 
		'  exception '|| 
		'  when others then '|| 
		'   rollback; ' || 
		'   DBMS_SESSION.CLOSE_DATABASE_LINK(''tasy_aghos''); ' ||				 
		'  end; '||			 
		'end;';
		 
CALL exec_sql_dinamico_bv('AGHOS', ds_comando_w, 	'nr_internacao_p='		|| nr_internacao_p 		|| ds_sep_bv_w || 
						'cd_cnes_solicitante_p='	|| cd_cnes_solicitante_w 	|| ds_sep_bv_w || 
						'ie_tipo_evolucao_p='		|| ie_tipo_evolucao_w	 	|| ds_sep_bv_w || 
						'ds_evolucao_p='		|| ds_evolucao_w		|| ds_sep_bv_w || 
						'ds_usuario_p='			|| ds_usuario_w);
 
commit;
 
end;
