CREATE OR REPLACE PROCEDURE agrupar_itens_requisicao ( nr_requisicao_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w			integer;
cd_material_w			integer;
qt_material_requisitada_w	double precision;
qt_estoque_w			double precision;

c01 CURSOR FOR
	SELECT	min(nr_sequencia),
		sum(qt_material_requisitada),
		sum(qt_estoque),
		cd_material
	from	item_requisicao_material
	where	nr_requisicao = nr_requisicao_p
	and	coalesce(nr_seq_lote_fornec::text, '') = ''
	and	coalesce(cd_cgc_fornecedor::text, '') = ''
	and	coalesce(nr_seq_kit_estoque::text, '') = ''
	and	coalesce(cd_motivo_baixa,0) = 0
	and	coalesce(qt_material_atendida,0) = 0
	group by cd_material;


BEGIN
begin
	open C01;
	loop
	fetch C01 into
		nr_sequencia_w,
		qt_material_requisitada_w,
		qt_estoque_w,
		cd_material_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		update	item_requisicao_material
		set	qt_material_requisitada = qt_material_requisitada_w,
			qt_estoque = qt_estoque_w
		where	nr_requisicao = nr_requisicao_p
		and	nr_sequencia = nr_sequencia_w;

		delete	from item_requisicao_material
		where	nr_requisicao = nr_requisicao_p
		and	nr_sequencia <> nr_sequencia_w
		and	coalesce(nr_seq_lote_fornec::text, '') = ''
		and	coalesce(cd_cgc_fornecedor::text, '') = ''
		and	coalesce(nr_seq_kit_estoque::text, '') = ''
		and	coalesce(cd_motivo_baixa,0) = 0
		and	coalesce(qt_material_atendida,0) = 0
		and	cd_material = cd_material_w;

		end;
	end loop;
	close C01;

	commit;
exception
when others then
	null;
end;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
