CREATE OR REPLACE PROCEDURE agrupar_protocolo_convenio ( nr_seq_protocolo_origem_p bigint, nr_seq_protocolo_destino_p bigint, nr_protocolo_p text, nm_usuario_p text) AS $body$
DECLARE

 
 
nr_interno_conta_w	bigint;
ds_log_w		varchar(2000);
qt_regra_w		bigint;
qt_ctas_prot_orig_w	bigint;
qt_ctas_prot_dest_w	bigint;
ie_status_protocolo_w	varchar(02);


BEGIN 
 
select	ie_status_protocolo 
into STRICT	ie_status_protocolo_w 
from 	protocolo_convenio 
where	nr_seq_protocolo	= nr_seq_protocolo_origem_p;
 
if (ie_status_protocolo_w	= 2) then 
	-- O protocolo está com status definitivo, não pode ser agrupado! 
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(232934);
end if;
 
 
select	coalesce(max(Obter_Qtde_Contas_Protocolo(cd_convenio, ie_tipo_protocolo, cd_estabelecimento)),0) 
into STRICT	qt_regra_w 
from 	protocolo_convenio 
where 	nr_seq_protocolo 	= nr_seq_protocolo_destino_p;
 
 
Select 	count(*) 
into STRICT	qt_ctas_prot_orig_w 
from 	conta_paciente 
where 	nr_seq_protocolo	= nr_seq_protocolo_origem_p;
 
Select 	count(*) 
into STRICT	qt_ctas_prot_dest_w 
from 	conta_paciente 
where 	nr_seq_protocolo	= nr_seq_protocolo_destino_p;
 
if (qt_regra_w > 0) then 
	begin 
 
	if	((qt_ctas_prot_dest_w + qt_ctas_prot_orig_w) > qt_regra_w) then 
		-- Limite de contas excedido 
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(232935);
	end if;
 
	end;
end if;
 
update	conta_paciente 
set 	nr_protocolo		=	nr_protocolo_p, 
	nr_seq_protocolo	=	nr_seq_protocolo_destino_p 
where	nr_seq_protocolo	=	nr_seq_protocolo_origem_p;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
