CREATE OR REPLACE PROCEDURE agserv_registra_cancelamento ( nr_seq_agenda_p bigint, cd_motivo_cancelamento_p text, ds_observacao_p text) AS $body$
DECLARE


ds_observacao_w		varchar(2000);
dt_atual_w			timestamp;


BEGIN

update	agenda_consulta a
set	a.ie_status_agenda	 = 'C',
	a.cd_motivo_cancelamento = cd_motivo_cancelamento_p,
	a.dt_cancelamento	= clock_timestamp()
where	a.nr_sequencia		= nr_seq_agenda_p;

if (ds_observacao_p IS NOT NULL AND ds_observacao_p::text <> '') then
	update	agenda_consulta a
	set	a.ds_observacao		 = substr(a.ds_observacao||chr(10)|| wheb_mensagem_pck.get_texto(791583) ||': '||ds_observacao_p,1,2000)
	where	a.nr_sequencia		= nr_seq_agenda_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
