CREATE OR REPLACE PROCEDURE agserv_verf_agend_lista_espera ( cd_agenda_p bigint, cd_pessoa_fisica_p text, nm_paciente_p text, dt_agenda_p timestamp, ds_consistencia_p INOUT text) AS $body$
DECLARE

 
qt_pac_lista_espera_w	bigint;
ds_mensagem_w		varchar(2000);
			

BEGIN 
ds_consistencia_p := null;
-- verifica se existe paciente em Lista de Espera no mesmo horário, com status Aguardando, caso o cd_pessoa_fisica_p e o nm_paciente_p sejam diferentes; 
select	count(*) 
into STRICT	qt_pac_lista_espera_w 
from 	agenda_lista_espera a 
where	a.cd_agenda = cd_agenda_p 
and (a.cd_pessoa_fisica <> cd_pessoa_fisica_p 
or	a.nm_pessoa_lista <> nm_paciente_p) 
and	a.dt_desejada = dt_agenda_p 
and	a.ie_status_espera = 'A';
 
 
--se existir paciente em lista de espera para o mesmo horário o agendamento do paciente será bloqueado para que o mesmo não passe na frente dos que estão em lista de espera; 
if (qt_pac_lista_espera_w > 0) then	 
	ds_mensagem_w := obter_desc_expressao(774809);	
end if;
 
ds_consistencia_p := ds_mensagem_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
