CREATE OR REPLACE PROCEDURE ag_exame_insere_obs_bloq ( cd_agenda_p bigint, dt_agenda_p timestamp, ds_observacao_p INOUT text) AS $body$
DECLARE



ds_observacao_w	varchar(255) := '';

C01 CURSOR FOR
	SELECT 	a.ds_observacao
	FROM 	(	SELECT	b.nr_sequencia,
				b.ds_observacao
			FROM   	agenda_bloqueio b,
				agenda_paciente a
			WHERE  	b.cd_agenda    = cd_agenda_p
			AND	a.cd_agenda    = b.cd_agenda
			AND   	dt_agenda_p BETWEEN b.dt_inicial AND b.dt_final
			AND 	((obter_cod_dia_semana(dt_agenda_p) = b.ie_dia_semana) OR ((b.ie_dia_semana = 9) and b.ie_dia_semana not in (1,7)) or (coalesce(b.ie_dia_semana::text, '') = ''))
			AND substr(obter_valor_dominio(1007, b.ie_motivo_bloqueio),1,200) = (select max(substr(obter_valor_dominio(1007, IE_MOTIVO_BLOQUEIO),1,200))
																				from   agenda_bloqueio
																				WHERE  	cd_agenda    = cd_agenda_p
																				AND   	dt_agenda_p BETWEEN dt_inicial AND dt_final)
			ORDER BY	b.nr_sequencia DESC) a LIMIT 1;


BEGIN

open C01;
loop
fetch C01 into
	ds_observacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_observacao_p := ds_observacao_w;
	end;
end loop;
close C01;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
