CREATE OR REPLACE PROCEDURE ai_confirmar_horarios_ageint ( nr_seq_ageint_p bigint, ie_transferencia_p text, ie_forma_apres_check_list_p text, ie_check_list_p INOUT text, ds_erro_p INOUT text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

		 
ds_erro_w	varchar(2000);
ie_check_list_w	varchar(1);


BEGIN 
	ds_erro_w := ageint_consistir_idade_imc(nr_seq_ageint_p, cd_estabelecimento_p, null, nm_usuario_p, ds_erro_w);
	if (coalesce(ds_erro_w::text, '') = '') then 
		begin 
		ds_erro_w := ageint_consistir_campo_obrig(nr_seq_ageint_p, cd_estabelecimento_p, nm_usuario_p, ds_erro_w);
		if (coalesce(ds_erro_w::text, '') = '') then 
			begin 
			if (ie_transferencia_p = 'N') and (ie_forma_apres_check_list_p = 'C') then 
				begin 
					CALL ageint_check_list_confirmacao(nr_seq_ageint_p, nm_usuario_p, cd_estabelecimento_p);		
					select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
					into STRICT	ie_check_list_w 
					from	ageint_check_list_paciente 
					where	nr_seq_ageint = nr_seq_ageint_p;
				end;
			end if;
			end;
		end if;
		end;
	end if;
 
ie_check_list_p := ie_check_list_w;
ds_erro_p	:= ds_erro_w;	
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
