SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ajustar_cid_haertel ( cd_medico_p text, nm_usuario_p text) AS $body$
DECLARE


qt_table_w		bigint;
ds_comando_create_w	varchar(1000);
ds_comando_insert_w	varchar(1000);
nr_seq_cli_w		bigint;
nr_seq_cid_w		bigint;
dt_atualizacao_w	varchar(20) := to_char(clock_timestamp(),'dd/mm/yyyy hh24:mi:ss');

c01 CURSOR FOR
SELECT	a.nr_sequencia nr_seq_cli,
	b.nr_sequencia nr_seq_cid
from	med_pac_cid b,
	med_cliente a
where	upper(b.cd_doenca_cid) = 'R730'
and	b.nr_seq_cliente = a.nr_sequencia
and	a.cd_medico = cd_medico_p
order by
	nr_seq_cli,
	nr_seq_cid;


BEGIN
select	coalesce(count(*),0)
into STRICT	qt_table_w
from	tab
where	upper(tname) = 'MED_AJUSTE_CID';

if (qt_table_w = 0) then
	ds_comando_create_w	:=	'create	table	MED_AJUSTE_CID	('||
					'						cd_medico		varchar2(10),		'||
					'						nr_seq_cli		number(10,0),		'||
					'						nr_seq_cid		number(10,0),		'||
					'						dt_atualizacao	varchar2(20),		'||
					'						nm_usuario		varchar2(15))		';
	CALL exec_sql_dinamico('TASY', ds_comando_create_w);
	commit;
end if;

open c01;
loop
fetch c01 into	nr_seq_cli_w,
			nr_seq_cid_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	ds_comando_insert_w	:=	'insert	into	med_ajuste_cid	('||
					'						cd_medico,					'||
					'						nr_seq_cli,					'||
					'						nr_seq_cid,					'||
					'						dt_atualizacao,				'||
					'						nm_usuario)					'||
					'			values			(						'||
											CHR(39) || cd_medico_p || CHR(39)	 	 ||','||
											nr_seq_cli_w			  	 	 ||','||
											nr_seq_cid_w			  	 	 ||','||
											CHR(39) || dt_atualizacao_w   || CHR(39)  ||','||
											CHR(39) || nm_usuario_p || CHR(39) ||')';
	CALL exec_sql_dinamico('TASY', ds_comando_insert_w);

	update	med_pac_cid
	set	cd_doenca_cid = 'R739'
	where	nr_sequencia = nr_seq_cid_w;
	end;
end loop;
close c01;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
