CREATE OR REPLACE PROCEDURE ajustar_cirurgia_partic ( nr_sequencia_p bigint, nr_cirurgia_p bigint) AS $body$
DECLARE
	-- Change this to reflect the dblink connection string
	v_conn_str  text := format('port=%s dbname=%s user=%s', current_setting('port'), current_database(), current_user);
	v_query     text;

BEGIN
	v_query := 'CALL ajustar_cirurgia_partic_atx ( ' || quote_nullable(nr_sequencia_p) || ',' || quote_nullable(nr_cirurgia_p) || ' )';
	PERFORM * FROM dblink(v_conn_str, v_query) AS p (ret boolean);

