CREATE OR REPLACE PROCEDURE ajustar_cirurgia_partic_atx ( nr_sequencia_p bigint, nr_cirurgia_p bigint) AS $body$
DECLARE


qt_cir_partic_w		bigint;
BEGIN

select	count(*)
into STRICT	qt_cir_partic_w
from	cirurgia_participante
where	nr_seq_procedimento = nr_sequencia_p
and 	nr_cirurgia	= nr_cirurgia_p;

if (qt_cir_partic_w > 0) then
	CALL wheb_usuario_pck.set_ie_executar_trigger('N');

	update	cirurgia_participante
	set	nr_seq_procedimento  = NULL
	where 	nr_seq_procedimento = nr_sequencia_p
	and 	nr_cirurgia	= nr_cirurgia_p;

	CALL wheb_usuario_pck.set_ie_executar_trigger('S');
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
