CREATE OR REPLACE PROCEDURE ajustar_conta_vazia_geral (NM_USUARIO_P text) AS $body$
DECLARE


qt_reg_procedimento_w	bigint;
qt_reg_material_w	bigint;	
nr_interno_conta_w	bigint;

C01 CURSOR FOR
	SELECT	nr_interno_conta
	from	conta_paciente a
	where	ie_status_acerto = 1
	and 	dt_mesano_referencia > clock_timestamp() - interval '60 days'
	and 	not exists (SELECT 1 from procedimento_paciente x where x.nr_interno_conta = a.nr_interno_conta)
	and 	not exists (select 1 from material_atend_paciente y where y.nr_interno_conta = a.nr_interno_conta)
	order by 1;
			

BEGIN

open C01;
loop
fetch C01 into	
	nr_interno_conta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	
	/* Deletar contas sem procedimentos e materiais */

	begin
	select 	count(*)
	into STRICT	qt_reg_procedimento_w		
	from 	procedimento_paciente
	where	nr_interno_conta		= nr_interno_conta_w;
	exception
		when others then
		qt_reg_procedimento_w := 0;
	end;

	begin
	select 	count(*)
	into STRICT	qt_reg_material_w		
	from 	material_atend_paciente
	where	nr_interno_conta		= nr_interno_conta_w;
	exception
		when others then
		qt_reg_material_w := 0;
	end;

	if (qt_reg_material_w + qt_reg_procedimento_w) = 0 then
		begin
		delete from conta_paciente
		where nr_interno_conta = nr_interno_conta_w
		and ((Obter_Tipo_Convenio(cd_convenio_parametro) <> 3) or (obter_se_contido_char(ds_observacao,'-Reinternação AIH 72 horas.') = 'N'));
		exception
			when others then
				qt_reg_procedimento_w	:= 0;
		end;
	end if;
	
	end;
end loop;
close C01;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
