CREATE OR REPLACE PROCEDURE ajustar_conta_vazia ( nr_atendimento_p bigint, NM_USUARIO_P text) AS $body$
DECLARE


ie_existe_registro_w	char(1);
nr_interno_conta_w	bigint;

C01 CURSOR FOR
	SELECT	nr_interno_conta
	from	conta_paciente
	where	nr_atendimento = nr_atendimento_p
	and 	ie_status_acerto = 1
	order by nr_interno_conta;


BEGIN

open C01;
loop
fetch C01 into
	nr_interno_conta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	/* Deletar contas sem procedimentos e materiais */

	select 	coalesce(max('S'),'N')
	into STRICT	ie_existe_registro_w
	from 	procedimento_paciente where		nr_interno_conta = nr_interno_conta_w LIMIT 1;

	if (ie_existe_registro_w = 'N') then
		select 	coalesce(max('S'),'N')
		into STRICT	ie_existe_registro_w
		from 	material_atend_paciente where		nr_interno_conta		= nr_interno_conta_w LIMIT 1;

		if (ie_existe_registro_w = 'N') then
			begin
			delete from conta_paciente
			where nr_interno_conta = nr_interno_conta_w
			and ((Obter_Tipo_Convenio(cd_convenio_parametro) <> 3) or (obter_se_contido_char(ds_observacao,obter_desc_expressao(342126)) = 'N'));
			exception when others then
				null;
			end;
		end if;
	end if;

	end;
end loop;
close C01;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
