SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ajustar_data_entrega_prescr ( nr_prescricao_p bigint, nm_usuario_p text, ie_processo_lab_p text) AS $body$
DECLARE





nr_sequencia_w			bigint;
dt_prev_execucao_w		timestamp;
nr_seq_proc_interno_w		bigint;
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
cd_setor_atendimento_w		bigint;
dt_resultado_w			timestamp;
nr_seq_exame_w			bigint;
ie_Lab_Setor_Solicitante_w		varchar(10);
cd_setor_solicitacao_w		bigint;
cd_setor_entrega_w		bigint;
cd_material_coleta_w		varchar(20);
qt_dia_entrega_w			double precision;
ie_emite_mapa_w			varchar(10);
ds_hora_fixa_w			varchar(255);
ie_data_resultado_w		varchar(255);
ie_atualizar_recoleta_w		varchar(255);
qt_min_entrega_w			double precision;
cd_setor_retorno_w			varchar(255);
ie_urgencia_w			varchar(2);
ie_dia_semana_final_w 		bigint;
qt_min_atraso_w			bigint;
ie_forma_atual_dt_result_w	exame_lab_regra_setor.ie_atul_data_result%type;

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.dt_prev_execucao,
		a.nr_seq_proc_interno,
		a.cd_procedimento,
		a.ie_origem_proced,
		coalesce(a.cd_setor_atendimento,coalesce(b.cd_setor_entrega,b.cd_setor_atendimento)),
		a.dt_resultado,
		a.nr_seq_exame,
		b.cd_setor_entrega,
		a.cd_material_exame,
		a.ie_urgencia
	from	prescr_procedimento a,
		prescr_medica b
	where	a.nr_prescricao	= b.nr_prescricao
	and	a.nr_prescricao	= nr_prescricao_p;


BEGIN

ie_Lab_Setor_Solicitante_w	:= Obter_Valor_Param_Usuario(924, 307, Obter_Perfil_Ativo, nm_usuario_p, 0);

if (ie_processo_lab_p = 'N') then
	update	prescr_medica
	set	dt_entrega	= dt_prescricao
	where	nr_prescricao	= nr_prescricao_p;
end if;

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	dt_prev_execucao_w,
	nr_seq_proc_interno_w,
	cd_procedimento_w,
	ie_origem_proced_w,
	cd_setor_atendimento_w,
	dt_resultado_w,
	nr_seq_exame_w,
	cd_setor_entrega_w,
	cd_material_coleta_w,
	ie_urgencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	begin
	if (coalesce(nr_seq_exame_w::text, '') = '') and (cd_procedimento_w IS NOT NULL AND cd_procedimento_w::text <> '') then

		update	prescr_medica
		set	dt_entrega	= coalesce(dt_resultado_w,dt_prescricao)
		where	nr_prescricao	= nr_prescricao_p;

		dt_resultado_w := Obter_Entrega_Laudo(	nr_prescricao_p, dt_prev_execucao_w, nr_seq_proc_interno_w, cd_procedimento_w, ie_origem_proced_w, cd_setor_atendimento_w, nm_usuario_p, dt_resultado_w);


		if (dt_resultado_w IS NOT NULL AND dt_resultado_w::text <> '') then
			update	prescr_procedimento
			set	dt_resultado	= dt_resultado_w
			where	nr_prescricao	= nr_prescricao_p
			and	nr_sequencia	= nr_sequencia_w;
		end if;
	end if;

	if (nr_seq_exame_w IS NOT NULL AND nr_seq_exame_w::text <> '') then
		if (ie_Lab_Setor_Solicitante_w	= 'E') then
			cd_setor_solicitacao_w	:= cd_setor_entrega_w;
		else
			cd_setor_solicitacao_w	:= wheb_usuario_pck.get_cd_setor_atendimento;
		end if;


		update	prescr_medica
		set	dt_entrega	= coalesce(dt_resultado_w,dt_prescricao)
		where	nr_prescricao	= nr_prescricao_p
		and	dt_entrega < dt_resultado_w;

		SELECT * FROM Obter_Setor_Exame_Lab(	nr_prescricao_p, nr_seq_exame_w, cd_setor_solicitacao_w, cd_material_coleta_w, dt_prev_execucao_w, 'S', cd_setor_retorno_w, cd_setor_retorno_w, cd_setor_retorno_w, qt_dia_entrega_w, ie_emite_mapa_w, ds_hora_fixa_w, ie_data_resultado_w, qt_min_entrega_w, ie_atualizar_recoleta_w, ie_urgencia_w, ie_dia_semana_final_w, ie_forma_atual_dt_result_w, qt_min_atraso_w) INTO STRICT cd_setor_retorno_w, cd_setor_retorno_w, cd_setor_retorno_w, qt_dia_entrega_w, ie_emite_mapa_w, ds_hora_fixa_w, ie_data_resultado_w, qt_min_entrega_w, ie_atualizar_recoleta_w, ie_dia_semana_final_w, ie_forma_atual_dt_result_w, qt_min_atraso_w;


	end if;
	exception
		when others then
			null;
	end;
end loop;
close C01;

if (ie_processo_lab_p = 'S') and (dt_resultado_w IS NOT NULL AND dt_resultado_w::text <> '') then
	update	prescr_medica
	set	dt_entrega	= dt_prescricao
	where	nr_prescricao	= nr_prescricao_p
	and	dt_entrega < dt_resultado_w;
else
	update	prescr_medica
	set	dt_entrega	= dt_prescricao
	where	nr_prescricao	= nr_prescricao_p;
end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ajustar_data_entrega_prescr ( nr_prescricao_p bigint, nm_usuario_p text, ie_processo_lab_p text) FROM PUBLIC;

