SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ajustar_difrenca_rateio (nr_seq_retorno_p bigint, nm_usuario_p text, vl_total_p bigint, ie_opcao_p bigint) AS $body$
DECLARE


vl_amaior_total_w	double precision;
vl_diferenca_w		double precision;
nr_seq_ret_glosa_w	bigint;


BEGIN

if (ie_opcao_p = 2) then

	select	sum(a.vl_amaior)
	into STRICT	vl_amaior_total_w
	from	convenio_retorno_glosa a,
		convenio_retorno_item c,
		w_ret_glosa b
	where	b.nr_seq_ret_glosa	= a.nr_sequencia
	and	c.nr_sequencia	= a.nr_seq_ret_item
	and	c.nr_seq_retorno	= nr_seq_retorno_p
	and	b.nm_usuario		= nm_usuario_p;

	vl_diferenca_w	:= coalesce(vl_total_p,0) - coalesce(vl_amaior_total_w,0);

	if (vl_diferenca_w > 0) then

		select	max(a.nr_sequencia)
		into STRICT	nr_seq_ret_glosa_w
		from	convenio_retorno_glosa a,
			convenio_retorno_item c,
			w_ret_glosa b
		where	b.nr_seq_ret_glosa	= a.nr_sequencia
		and	c.nr_sequencia	= a.nr_seq_ret_item
		and	c.nr_seq_retorno	= nr_seq_retorno_p
		and	a.vl_amaior	> 0
		and	b.nm_usuario	= nm_usuario_p;

		update	convenio_retorno_glosa
		set	vl_amaior		= vl_amaior + vl_diferenca_w
		where	nr_sequencia	= nr_seq_ret_glosa_w;

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
