CREATE OR REPLACE PROCEDURE ajustar_dispensacao_qtd ( nr_sequencia_p bigint, nm_usuario_p text, nr_prescricao_p bigint, ie_tipo_prescr_p bigint, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
/* 
1 - Normal 
2 - Materiais consignados 
*/
 
					 
cd_material_w		integer;
qt_dispensacao_w	double precision;
nr_seq_lote_fornec_w	bigint;
cd_local_estoque_w	varchar(4) := null;
nr_cirurgia_w		bigint;
				
C01 CURSOR FOR 
	SELECT	a.cd_material, 
		a.qt_dispensacao, 
		CASE WHEN a.nr_seq_lote_fornec=0 THEN null  ELSE a.nr_seq_lote_fornec END  
	from	ajuste_dispensacao_pepo a 
	where	a.nr_sequencia	= nr_sequencia_p;				
 

BEGIN 
 
if (ie_tipo_prescr_p = 2) then 
	cd_local_estoque_w := obter_param_usuario(872, 150, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, cd_local_estoque_w);
end if;
 
select	max(nr_cirurgia) 
into STRICT	nr_cirurgia_w 
from	cirurgia 
where	nr_prescricao	=	nr_prescricao_p;	
 
open C01;
loop 
fetch C01 into	 
	cd_material_w, 
	qt_dispensacao_w, 
	nr_seq_lote_fornec_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	if (coalesce(nr_seq_lote_fornec_w::text, '') = '') then 
		select	max(a.nr_seq_lote_fornec) 
		into STRICT	nr_seq_lote_fornec_w 
		from	cirurgia_agente_disp a 
		where	a.nr_cirurgia	=	nr_cirurgia_w 
		and	a.cd_material	=	cd_material_w 
		and	not exists (	SELECT	1 
					from	prescr_material b 
					where	b.nr_prescricao	=	nr_prescricao_p 
					and	b.cd_material	=	cd_material_w 
					and	b.nr_seq_lote_fornec = a.nr_seq_lote_fornec);
	end if;
	 
	if (coalesce(nr_seq_lote_fornec_w::text, '') = '') then 
		select	max(a.nr_seq_lote_fornec) 
		into STRICT	nr_seq_lote_fornec_w 
		from	cirurgia_agente_disp a 
		where	a.nr_cirurgia	=	nr_cirurgia_w 
		and	a.cd_material	=	cd_material_w;
	end if;
	 
	if (nr_seq_lote_fornec_w = 0) then 
		nr_seq_lote_fornec_w := null;
	end if;
	 
	CALL gravar_mat_adic_barras(	nr_prescricao_p, 
				nr_seq_lote_fornec_w, 
				cd_material_w, 
				campo_numerico(cd_local_estoque_w), 
				qt_dispensacao_w, 
				nm_usuario_p, 
				cd_estabelecimento_p, 
				'GI', 
				null, 
				null, 
				null, 
				null, 
				null, 
				null, 
				'S');	
	end;
end loop;
close C01;
 
--atualiza_disp_cirurgia(nr_cirurgia_w); 
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ajustar_dispensacao_qtd ( nr_sequencia_p bigint, nm_usuario_p text, nr_prescricao_p bigint, ie_tipo_prescr_p bigint, cd_estabelecimento_p bigint) FROM PUBLIC;

