CREATE OR REPLACE PROCEDURE ajustar_ds_origem_ap_lote () AS $body$
DECLARE


qt_registros_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_registros_w
from	dominio
where	cd_dominio = '8985';

if (qt_registros_w > 0) then
	begin

	/*PD              Padrão
	wheb_mensagem_pck.get_texto(802231)*/
	update	ap_lote
	set	ds_origem_ger_lote = 'PD'
	where	ds_origem_ger_lote in ('Padrão','Estándar','Default','Standard');
	commit;

	/*RE              Reaprazado
	wheb_mensagem_pck.get_texto(1033347)*/
	update	ap_lote
	set	ds_origem_ger_lote = 'RE'
	where	ds_origem_ger_lote in ('Reaprazado','Volver a ser aplazado','Rescheduled','Termin verschoben');
	commit;

	/*AC              Alto custo
	obter_desc_expressao(283399)*/
	update	ap_lote
	set	ds_origem_ger_lote = 'AC'
	where	ds_origem_ger_lote in ('Alto custo','Alto costo','High cost ','High cost','Teuer');
	commit;

	/*AR              Alto risco
	wheb_mensagem_pck.get_texto(790872)*/
	update	ap_lote
	set	ds_origem_ger_lote = 'AR'
	where	ds_origem_ger_lote in ('Alto risco','Alto riesgo','High risk ','High risk','Hohes Risiko');
	commit;

	/*CO              Controlado
	wheb_mensagem_pck.get_texto(799668)*/
	update	ap_lote
	set	ds_origem_ger_lote = 'CO'
	where	ds_origem_ger_lote in ('Controlado','Controlled','Kontrolliert');
	commit;

	/*NU              Nutrição enteral
	wheb_mensagem_pck.get_Texto(310995)*/
	update	ap_lote
	set	ds_origem_ger_lote = 'NU'
	where	ds_origem_ger_lote in ('Nutrição enteral','Nutrición enteral','Enteral nutrition ','Enteral nutrition','Enterale Ernährung ','Enterale Ernährung');
	commit;

	/*SO              Solução separado
	wheb_mensagem_pck.get_texto(364236)*/
	update	ap_lote
	set	ds_origem_ger_lote = 'SO'
	where	ds_origem_ger_lote in ('Solução separado','Solución separada','Solution separated','Lösung getrennt');
	commit;

	/*TE              Termolábil
	Wheb_mensagem_pck.get_texto(799622)*/
	update	ap_lote
	set	ds_origem_ger_lote = 'TE'
	where	ds_origem_ger_lote in ('Termolábil','Termolábil','Thermolabile','Thermolabil');
	commit;

	/*LI              Lote individual
	wheb_mensagem_pck.get_texto(790876)*/
	update	ap_lote
	set	ds_origem_ger_lote = 'LI'
	where	ds_origem_ger_lote in ('Lote individual','Lote individual','Individual batch','Individueller Stapel');
	commit;

	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
