SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ajustar_entrega_lib_prescr ( nm_usuario_p text, nr_prescricao_p bigint) AS $body$
DECLARE


ie_ajustar_entrega_w		varchar(10);
dt_entrega_w			timestamp;


BEGIN

if (coalesce(nr_prescricao_p,0) > 0) then

	select	max(dt_resultado)
	into STRICT	dt_entrega_w
	from	prescr_procedimento
	where	nr_prescricao = nr_prescricao_p;

	if (dt_entrega_w IS NOT NULL AND dt_entrega_w::text <> '') then
		update 	prescr_medica
		set	dt_entrega     = dt_entrega_w,
			nm_usuario     = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where	nr_prescricao = nr_prescricao_p;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
