SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ajustar_exclu_lote_diluicao ( nr_prescricao_p bigint, nr_sequencia_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


/*
D - Diluicao
R - Rediluicao
*/
BEGIN

update	prescr_material
set	ds_diluicao_edit	 = NULL
where	nr_prescricao		= nr_prescricao_p
and	nr_sequencia		= nr_sequencia_p;

if (ie_opcao_p = 'D') then
	update	ap_lote_item
	set	dt_supensao		=	clock_timestamp(),
		nm_usuario_susp		=	nm_usuario_p,
		nr_seq_mat_hor		 = NULL
	where	nr_seq_mat_hor in (	SELECT	b.nr_sequencia
					from	prescr_mat_hor b,
						prescr_material a
					where 	a.nr_prescricao			= b.nr_prescricao
					and	a.nr_sequencia			= b.nr_seq_material
					and	a.nr_prescricao 		= nr_prescricao_p
					and 	a.nr_sequencia_diluicao 	= nr_sequencia_p
					and 	a.ie_agrupador = 3);
elsif (ie_opcao_p = 'R') then
	update	ap_lote_item
	set	dt_supensao		=	clock_timestamp(),
		nm_usuario_susp		=	nm_usuario_p,
		nr_seq_mat_hor		 = NULL
	where	nr_seq_mat_hor in (	SELECT	b.nr_sequencia
					from	prescr_mat_hor b,
						prescr_material a
					where 	a.nr_prescricao			= b.nr_prescricao
					and	a.nr_sequencia			= b.nr_seq_material
					and	a.nr_prescricao 		= nr_prescricao_p
					and 	a.nr_sequencia_diluicao 	= nr_sequencia_p
					and 	a.ie_agrupador = 7);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
