CREATE OR REPLACE PROCEDURE ajustar_folhas_ipe_exames ( nr_folha_p bigint, nr_interno_conta_p bigint, nr_seq_protocolo_p bigint, nr_linha_retorno_p INOUT bigint) AS $body$
DECLARE



qt_contas_folha_w		bigint;
nr_sequencia_w		bigint;
nr_linha_w		bigint;
qt_posicao_item_w		bigint := 0;
nr_linha_ant_w		bigint;

c01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_linha
	from	w_interf_conta_item_ipe
	where	nr_seq_protocolo 	= nr_seq_protocolo_p
	and 	nr_interno_conta 	= nr_interno_conta_p
	and 	nr_folha 		= nr_folha_p
	order by 	nr_linha;


BEGIN

nr_linha_w:= 0;

select 	count(distinct nr_interno_conta)
into STRICT	qt_contas_folha_w
from 	w_interf_conta_item_ipe
where 	nr_seq_protocolo 	= nr_seq_protocolo_p
and 	nr_folha 		= nr_folha_p;

if (qt_contas_folha_w > 1) then
	open c01;
	loop
	fetch c01 into
		nr_sequencia_w,
		nr_linha_ant_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		nr_linha_w:= nr_linha_w + 1;

		update 	w_interf_conta_item_ipe
		set 	nr_folha 			= nr_folha_p + 1,
		    	nr_linha			= nr_linha_w,
			nr_seq_conta_convenio	= nr_seq_conta_convenio + 1
		where  	nr_sequencia 		= nr_sequencia_w;

		select	count(*)
		into STRICT	qt_posicao_item_w
		from	w_posicao_item_nota_ipe
		where	nr_folha_item 		= nr_folha_p
		and	nr_interno_conta		= nr_interno_conta_p
		and	nr_seq_protocolo		= nr_seq_protocolo_p;

		if (coalesce(qt_posicao_item_w,0) > 0) then
			begin

			update	w_posicao_item_nota_ipe
			set	nr_folha_item	= nr_folha_p + 1,
				nr_linha_item	= nr_linha_w,
				nr_nota_item	= nr_nota_item + 1
			where	nr_linha_item	= nr_linha_ant_w
			and	nr_folha_item 	= nr_folha_p
			and	nr_interno_conta	= nr_interno_conta_p
			and	nr_seq_protocolo	= nr_seq_protocolo_p;

			end;
		end if;

		end;
	end loop;
	close c01;
end if;

if (nr_linha_w > 0) then
	nr_linha_retorno_p	:= nr_linha_w;
else
	nr_linha_retorno_p	:= 0;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
