SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ajustar_horario_agenda_cir ( nr_seq_agenda_p bigint, ie_status_agenda_p text) AS $body$
DECLARE


hr_inicio_w	timestamp;
cd_agenda_w	bigint;
qt_item_w	bigint;
ie_encaixe_w	varchar(2);


BEGIN

select	max(hr_inicio),
	max(cd_agenda)
into STRICT	hr_inicio_w,
	cd_agenda_w
from	agenda_paciente
where	nr_sequencia = nr_seq_agenda_p;

select 	count(*)
into STRICT	qt_item_w
from	agenda_paciente
where	cd_agenda 		= cd_agenda_w
and	hr_inicio 		= hr_inicio_w
and	ie_status_agenda	= ie_status_agenda_p
and	Obter_Se_Agenda_Encaixe(nr_sequencia) <> 'S'
and	ie_status_agenda	not in ('C','L')
and	nr_sequencia		<> nr_seq_agenda_p;

if (qt_item_w > 0) then
	ie_encaixe_w := Obter_Se_Agenda_Encaixe(nr_seq_agenda_p);

	if (ie_encaixe_w = 'S') then
		update	agenda_paciente
		set	hr_inicio	 = hr_inicio + 1/86400
		where	nr_sequencia	 = nr_seq_agenda_p;
		commit;
	end if;
end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
