CREATE OR REPLACE PROCEDURE ajustar_item_superior_agrup ( nr_prescricao_p bigint, nr_seq_material_p bigint, nr_agrupamento_p bigint, ie_item_superior_p INOUT text, ie_farmacia_enfermagem_p text, ie_existe_superior_p text, nm_usuario_p text) AS $body$
DECLARE


qt_superior_w		bigint;
nr_agrupamento_w	double precision;
cd_mat_sem_apresent_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_superior_w
from	prescr_material a
where	nr_prescricao		= nr_prescricao_p
and	nr_agrupamento		= nr_agrupamento_p
and	nr_sequencia		<> nr_seq_material_p
and	ie_item_superior	= 'S';

if (ie_farmacia_enfermagem_p = 'F') then

	select	nr_agrupamento,
		cd_mat_sem_apresent
	into STRICT	nr_agrupamento_w,
		cd_mat_sem_apresent_w
	from	prescr_material
	where	nr_prescricao		= nr_prescricao_p
	and	nr_sequencia		= nr_seq_material_p;

	if (cd_mat_sem_apresent_w IS NOT NULL AND cd_mat_sem_apresent_w::text <> '') then
		/*Se já existe um item superior para o novo agrupamento então deve-se atualizar como não sendo superior e levar os compostos para o novo agrupamento.*/

		if (ie_item_superior_p	= 'S') then

			if (qt_superior_w	> 0) then
				update	prescr_material
				set	ie_item_superior	= 'N',
					nm_usuario		= nm_usuario_p
				where	nr_prescricao		= nr_prescricao_p
				and	nr_sequencia		= nr_seq_material_p;
			end if;

			ie_item_superior_p		:= 'N'; /*Parâmetro para ser tratado na Gestão das prescrições de enfermagem, uma vez que lá a rotina é feita no beforePost, sendo assim o update da procedure não funcionaria*/
			update	prescr_material
			set	nr_agrupamento	= nr_agrupamento_p,
				nm_usuario	= nm_usuario_p
			where	nr_prescricao	= nr_prescricao_p
			and	cd_mat_sem_apresent = cd_mat_sem_apresent_w
			and	nr_agrupamento	= nr_agrupamento_w;

		elsif (ie_item_superior_p	= 'N') then

			ie_item_superior_p		:= 'N'; /*Parâmetro para ser tratado na Gestão das prescrições de enfermagem, uma vez que lá a rotina é feita no beforePost, sendo assim o update da procedure não funcionaria*/
			if (coalesce(qt_superior_w,0)	= 0) then
				ie_item_superior_p		:= 'S';
				update	prescr_material
				set	ie_item_superior	= 'S',
					nm_usuario		= nm_usuario_p
				where	nr_prescricao		= nr_prescricao_p
				and	nr_sequencia		= nr_seq_material_p;
			end if;

			update	prescr_material
			set	nr_agrupamento	= nr_agrupamento_p,
				nm_usuario	= nm_usuario_p
			where	nr_prescricao	= nr_prescricao_p
			and	cd_mat_sem_apresent = cd_mat_sem_apresent_w
			and	nr_agrupamento	= nr_agrupamento_w;

		else

			if (qt_superior_w	> 0) then
				update	prescr_material
				set	ie_item_superior	= 'N',
					nm_usuario		= nm_usuario_p
				where	nr_prescricao		= nr_prescricao_p
				and	nr_agrupamento		= nr_agrupamento_w
				and	ie_item_superior	= 'S';
			else
				update	prescr_material
				set	ie_item_superior	= 'S',
					nm_usuario		= nm_usuario_p
				where	nr_prescricao		= nr_prescricao_p
				and	nr_agrupamento		= nr_agrupamento_w
				and	ie_item_superior	= 'N';
			end if;

			update	prescr_material
			set	nr_agrupamento	= nr_agrupamento_p,
				nm_usuario	= nm_usuario_p
			where	nr_prescricao	= nr_prescricao_p
			and	cd_mat_sem_apresent = cd_mat_sem_apresent_w
			and	nr_agrupamento	= nr_agrupamento_w;
		end if;
	else

		if (qt_superior_w	> 0) then
			update	prescr_material
			set	ie_item_superior	= 'N',
				nm_usuario		= nm_usuario_p
			where	nr_prescricao		= nr_prescricao_p
			and	ie_item_superior	= 'S'
			and	nr_sequencia		= nr_seq_material_p;
		end if;

	end if;
elsif (ie_farmacia_enfermagem_p = 'E') then

	select	cd_mat_sem_apresent,
		nr_agrupamento
	into STRICT	cd_mat_sem_apresent_w,
		nr_agrupamento_w
	from	prescr_material
	where	nr_prescricao		= nr_prescricao_p
	and	nr_sequencia		= nr_seq_material_p;

	qt_superior_w	:= 0;
	if (ie_existe_superior_p	= 'S') then
		qt_superior_w	:= 1;
	end if;

	if (cd_mat_sem_apresent_w IS NOT NULL AND cd_mat_sem_apresent_w::text <> '') then
		/*Se já existe um item superior para o novo agrupamento então deve-se atualizar como não sendo superior e levar os compostos para o novo agrupamento.*/

		if (ie_item_superior_p	= 'S') then

			if (qt_superior_w	> 0) then
				update	prescr_material
				set	ie_item_superior	= 'N',
					nm_usuario		= nm_usuario_p
				where	nr_prescricao		= nr_prescricao_p
				and	nr_sequencia		= nr_seq_material_p;
			end if;

			update	prescr_material
			set	nr_agrupamento	= nr_agrupamento_w,
				nm_usuario	= nm_usuario_p
			where	nr_prescricao	= nr_prescricao_p
			and	cd_mat_sem_apresent = cd_mat_sem_apresent_w
			and	nr_agrupamento	= nr_agrupamento_p;

		elsif (ie_item_superior_p	= 'N') then

			if (coalesce(qt_superior_w,0)	= 0) then
				update	prescr_material
				set	ie_item_superior	= 'S',
					nm_usuario		= nm_usuario_p
				where	nr_prescricao		= nr_prescricao_p
				and	nr_sequencia		= nr_seq_material_p;
			end if;

			update	prescr_material
			set	nr_agrupamento	= nr_agrupamento_w,
				nm_usuario	= nm_usuario_p
			where	nr_prescricao	= nr_prescricao_p
			and	cd_mat_sem_apresent = cd_mat_sem_apresent_w
			and	nr_agrupamento	= nr_agrupamento_p;

		else

			if (qt_superior_w	> 0) then
				update	prescr_material
				set	ie_item_superior	= 'N',
					nm_usuario		= nm_usuario_p
				where	nr_prescricao		= nr_prescricao_p
				and	nr_agrupamento		= nr_agrupamento_p
				and	ie_item_superior	= 'S';
			else
				update	prescr_material
				set	ie_item_superior	= 'S',
					nm_usuario		= nm_usuario_p
				where	nr_prescricao		= nr_prescricao_p
				and	nr_agrupamento		= nr_agrupamento_p
				and	ie_item_superior	= 'N';
			end if;

			update	prescr_material
			set	nr_agrupamento	= nr_agrupamento_w,
				nm_usuario	= nm_usuario_p
			where	nr_prescricao	= nr_prescricao_p
			and	cd_mat_sem_apresent = cd_mat_sem_apresent_w
			and	nr_agrupamento	= nr_agrupamento_p;
		end if;
	else

		if (qt_superior_w	> 0) then
			update	prescr_material
			set	ie_item_superior	= 'N',
				nm_usuario		= nm_usuario_p
			where	nr_prescricao		= nr_prescricao_p
			and	ie_item_superior	= 'S'
			and	nr_sequencia		= nr_seq_material_p;
		end if;

	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
