SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ajustar_itens_auditoria (nr_sequencia_p bigint, qt_ajuste_p bigint, nr_seq_motivo_p bigint, ds_justificativa_p text, ie_tipo_p text, nm_usuario_p text) AS $body$
BEGIN

if (ie_tipo_p = 'M') then
	update	auditoria_matpaci
	set	nr_seq_motivo = CASE WHEN nr_seq_motivo_p=0 THEN NULL  ELSE nr_seq_motivo_p END ,
		qt_ajuste = qt_ajuste_p,
		ds_justificativa = CASE WHEN ds_justificativa_p = NULL THEN  ds_justificativa  ELSE substr(ds_justificativa || ' ' || ds_justificativa_p,1,255) END ,
		nm_usuario = nm_usuario_p,
		dt_atualizacao  = clock_timestamp()
	where 	nr_sequencia = nr_sequencia_p;
elsif (ie_tipo_p = 'P') then
	update	auditoria_propaci
	set	nr_seq_motivo = CASE WHEN nr_seq_motivo_p=0 THEN NULL  ELSE nr_seq_motivo_p END ,
		qt_ajuste = qt_ajuste_p,
		ds_justificativa = CASE WHEN ds_justificativa_p = NULL THEN  ds_justificativa  ELSE substr(ds_justificativa || ' ' || ds_justificativa_p,1,255) END ,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where 	nr_sequencia = nr_sequencia_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
