SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ajustar_itens_comp_gpe ( nr_prescricao_p bigint, nr_seq_material_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_agrupamento_w		double precision;
ie_via_aplicacao_w		varchar(5);
cd_intervalo_w			varchar(7);
hr_prim_horario_w		varchar(7);
ds_horarios_w			varchar(2000);
ie_dose_espec_agora_w		varchar(7);
qt_dose_especial_w		double precision;
nr_seq_item_w			bigint;
hr_dose_especial_w		varchar(7);
ie_urgencia_w			varchar(7);
ie_se_necessario_w		varchar(7);
ie_acm_w			varchar(7);
qt_min_aplic_dose_esp_w		smallint;
cd_estabelecimento_w		smallint;
cd_material_w			integer;
qt_unitaria_w			double precision;
nr_ocorrencias_w		double precision;
ie_origem_inf_w			varchar(1);
cd_unidade_medida_dose_w	varchar(30);
qt_material_w			double precision;
qt_dispensar_w			double precision;
ie_regra_disp_w			varchar(1);
ds_erro_w			varchar(2000);
qt_dias_util_W			bigint;
qt_dose_especial_ww		double precision;
ie_agrupador_w			smallint;

c01 CURSOR FOR
SELECT	nr_sequencia,
		cd_material,
		cd_unidade_medida_dose,
		qt_unitaria,
		qt_dias_util,
		CASE WHEN coalesce(ie_dose_espec_agora_w,'N')='S' THEN qt_dose_especial  ELSE null END
from	prescr_material
where	nr_prescricao	= nr_prescricao_p
and		nr_agrupamento	= nr_agrupamento_w
and		nr_sequencia	<> nr_seq_material_p
and 	ie_agrupador = 1;


BEGIN


	select	max(nr_agrupamento),
			max(ie_via_aplicacao),
			max(cd_intervalo),
			max(hr_prim_horario),
			max(ds_horarios),
			max(ie_dose_espec_agora),
			max(qt_dose_especial),
			max(hr_dose_especial),
			max(qt_min_aplic_dose_esp),
			coalesce(max(ie_urgencia),'N'),
			coalesce(max(ie_se_necessario),'N'),
			coalesce(max(ie_acm),'N'),
			max(ie_origem_inf),
			max(NR_OCORRENCIA),
			max(ie_agrupador)
	into STRICT	nr_agrupamento_w,
			ie_via_aplicacao_w,
			cd_intervalo_w,
			hr_prim_horario_w,
			ds_horarios_w,
			ie_dose_espec_agora_w,
			qt_dose_especial_w,
			hr_dose_especial_w,
			qt_min_aplic_dose_esp_w,
			ie_urgencia_w,
			ie_se_necessario_w,
			ie_acm_w,
			ie_origem_inf_w,
			nr_ocorrencias_w,
			ie_agrupador_w
	from	prescr_material
	where	nr_prescricao	= nr_prescricao_p
	and		nr_sequencia	= nr_seq_material_p;

if (ie_agrupador_w = 1) then
	open C01;
	loop
	fetch C01 into
		nr_seq_item_w,
		cd_material_w,
		cd_unidade_medida_dose_w,
		qt_unitaria_w,
		qt_dias_util_w,
		qt_dose_especial_ww;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		SELECT * FROM Obter_Quant_Dispensar(	cd_estabelecimento_p, cd_material_w, nr_prescricao_p, nr_seq_item_w, cd_intervalo_w, ie_via_aplicacao_w, qt_unitaria_w, qt_dose_especial_ww, nr_ocorrencias_w, null, ie_origem_inf_w, cd_unidade_medida_dose_w, qt_dias_util_w, qt_material_w, qt_dispensar_w, ie_regra_disp_w, ds_erro_w, ie_se_necessario_w, ie_acm_w) INTO STRICT qt_material_w, qt_dispensar_w, ie_regra_disp_w, ds_erro_w;

		update	prescr_material
		set		ie_via_aplicacao	= ie_via_aplicacao_w,
				cd_intervalo		= cd_intervalo_w,
				hr_prim_horario		= hr_prim_horario_w,
				ds_horarios		= ds_horarios_w,
				ie_dose_espec_agora	= ie_dose_espec_agora_w,
				qt_dose_especial	= coalesce(qt_dose_especial, qt_dose_especial_w),
				hr_dose_especial	= hr_dose_especial_w,
				qt_min_aplic_dose_esp	= qt_min_aplic_dose_esp_w,
				nm_usuario		= nm_usuario_p,
				dt_atualizacao		= clock_timestamp(),
				ie_urgencia		= ie_urgencia_w,
				ie_se_necessario	= ie_se_necessario_w,
				ie_acm			= ie_acm_w,
				qt_total_dispensar	= qt_dispensar_w,
				qt_material		= qt_material_w,
				ie_regra_disp		= CASE WHEN ie_regra_disp='D' THEN  ie_regra_disp  ELSE ie_regra_disp_w END ,
				nr_ocorrencia		= nr_ocorrencias_w
		where	nr_prescricao		= nr_prescricao_p
		and		nr_sequencia		= nr_seq_item_w;

	end loop;
	close C01;

	if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
