SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ajustar_local_item_sol ( nr_prescricao_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_retorno_w			integer;
nr_sequencia_solucao_w		prescr_solucao.nr_seq_solucao%type;
nr_seq_regra_local_w		regra_local_dispensacao.nr_sequencia%type;
cd_local_estoque_w		local_estoque.cd_local_estoque%type;

c01 CURSOR FOR
	SELECT	a.nr_sequencia_solucao
	from	prescr_material a
	where	a.nr_prescricao = nr_prescricao_p
	and	(a.nr_sequencia_solucao IS NOT NULL AND a.nr_sequencia_solucao::text <> '')
	and	((SELECT count(count(1))
		from	prescr_material x
		where	x.nr_prescricao = nr_prescricao_p
		and	x.nr_sequencia_solucao = a.nr_sequencia_solucao
		group by x.cd_local_estoque) > 1)
	group by a.nr_sequencia_solucao;


BEGIN

open c01;
loop
fetch c01 into
	nr_sequencia_solucao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select	coalesce(max(a.nr_seq_regra_local),0)
	into STRICT	nr_seq_regra_local_w
	from	prescr_material a
	where	a.nr_prescricao = nr_prescricao_p
	and	a.nr_sequencia_solucao = nr_sequencia_solucao_w
	and exists (	SELECT	1
			from	regra_local_dispensacao x
			where	x.nr_sequencia = a.nr_seq_regra_local
			and	x.ie_so_com_estoque = 'N');

	if (nr_seq_regra_local_w > 0) then
		select	cd_local_estoque
		into STRICT	cd_local_estoque_w
		from	regra_local_dispensacao
		where	nr_sequencia = nr_seq_regra_local_w;

		update	prescr_material
		set	cd_local_estoque = cd_local_estoque_w,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where	nr_prescricao = nr_prescricao_p
		and	nr_sequencia_solucao = nr_sequencia_solucao_w;
	end if;

	end;
end loop;
close c01;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
