SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ajustar_local_same ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_sequencia_w		bigint;
nr_sequencia_hist_w	bigint;
nr_seq_local_w		bigint;

C01 CURSOR FOR
SELECT	nr_sequencia
from	same_caixa
where	nr_seq_caixa_superior = nr_sequencia_p;

C02 CURSOR FOR
SELECT	nr_sequencia
from	same_prontuario
where	nr_seq_caixa = nr_sequencia_p
and	ie_status not in (2,5,6,10);



BEGIN

Select	max(nr_seq_local)
into STRICT	nr_seq_local_w
from	same_caixa
where	nr_sequencia = nr_sequencia_p;


OPEN C01;
LOOP
FETCH C01 into
	nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	Update	same_caixa
	set	nr_seq_local	= nr_seq_local_w,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_sequencia_w
	and	ie_situacao	= 'A';

	end;
END LOOP;
CLOSE C01;




OPEN C02;
LOOP
FETCH C02 into
	nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
	begin
	Update	same_prontuario
	set	nr_seq_local	= nr_seq_local_w,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_sequencia_w;


	Select	nextval('same_prontuario_hist_seq')
	into STRICT	nr_sequencia_hist_w
	;

	Insert into same_prontuario_hist(
		nr_sequencia,
		nr_seq_same,
		dt_atualizacao,
		nm_usuario,
		nr_seq_operacao,
		dt_historico,
		nr_seq_caixa,
		nr_seq_local,
		cd_pessoa_fisica)
	SELECT
		nr_sequencia_hist_w,
		nr_seq_same,
		clock_timestamp(),
		nm_usuario_p,
		9,
		clock_timestamp(),
		nr_seq_caixa,
		nr_seq_local_w,
		cd_pessoa_fisica
	from	same_prontuario_hist
	where	nr_sequencia = (SELECT	max(nr_sequencia)
				from	same_prontuario_hist
				where	nr_seq_same = nr_sequencia_w);


	end;
END LOOP;
CLOSE C02;


commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
