CREATE OR REPLACE PROCEDURE ajustar_motivo_item_audit ( nr_seq_auditoria_p bigint, cd_item_p bigint, cd_setor_atendimento_p bigint, dt_conta_p timestamp, nr_seq_motivo_p bigint, nm_usuario_p text, ie_tipo_item_p bigint) AS $body$
BEGIN

if (ie_tipo_item_p = 2) then

	update	auditoria_matpaci
	set	nr_seq_motivo = nr_seq_motivo_p,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_seq_auditoria = nr_seq_auditoria_p
	and	obter_cod_material_atend_pac(nr_seq_matpaci) = cd_item_p
	and	obter_setor_mat_auditoria(nr_seq_atepacu_ajuste, nr_seq_matpaci) = cd_setor_atendimento_p
	and	trunc(obter_data_matpaci(nr_seq_matpaci, 'C')) = trunc(dt_conta_p);

elsif (ie_tipo_item_p = 1) then

	update	auditoria_propaci
	set	nr_seq_motivo = nr_seq_motivo_p,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_seq_auditoria = nr_seq_auditoria_p
	and	obter_cod_procedimento_pac(nr_seq_propaci) = cd_item_p
	and	obter_setor_proc_auditoria(nr_seq_atepacu_ajuste, nr_seq_propaci) = cd_setor_atendimento_p
	and	trunc(obter_data_propaci(nr_seq_propaci, 'C')) = trunc(dt_conta_p);
end if;

end;
$body$
