SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ajustar_movto_indenizaveis () AS $body$
DECLARE


nr_seq_periodo_w		diops_periodo.nr_sequencia%type;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_periodo_w
from	diops_periodo
where	coalesce(dt_periodo_final,clock_timestamp()) between to_date('01/01/2017','dd/mm/yyyy') and fim_dia(to_date('31/03/2017','dd/mm/yyyy'))
and	ie_tipo_periodo_diops	= 'T'
and	(dt_fim_geracao IS NOT NULL AND dt_fim_geracao::text <> '');

if (coalesce(nr_seq_periodo_w,0) > 0) then
	delete	FROM diops_movto_pel	a
	where	a.nr_seq_periodo	= nr_seq_periodo_w
	and	a.nr_seq_tipo_movto	in (38,7,6,5,39,1,41,40,3);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ajustar_movto_indenizaveis () FROM PUBLIC;

