SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ajustar_parcela_cartao_cr ( nr_seq_parcela_p bigint, nr_seq_grupo_p bigint, vl_ajuste_p bigint, nm_usuario_p text, nr_seq_ext_movto_p bigint) AS $body$
DECLARE


nr_seq_bandeira_w		bigint;
vl_liquido_w			double precision;
ie_ajustar_valor_w		varchar(1);
nr_seq_movto_w			bigint;
vl_total_transacao_w		double precision;
cd_estabelecimento_w		bigint;
nr_seq_extrato_parcela_w	bigint;
vl_extrato_w			double precision;
vl_ajuste_w			double precision;
qt_sem_ajuste_w			bigint;
vl_total_original_w		double precision;
vl_total_parcela_w		double precision;
vl_ajuste_sobra_w		double precision;


BEGIN

select	max(b.nr_seq_bandeira),
	max(a.vl_liquido),
	max(b.nr_sequencia),
	max(b.cd_estabelecimento),
	max(a.nr_seq_extrato_parcela)
into STRICT	nr_seq_bandeira_w,
	vl_liquido_w,
	nr_seq_movto_w,
	cd_estabelecimento_w,
	nr_seq_extrato_parcela_w
from	movto_cartao_cr b,
	movto_cartao_cr_parcela a
where	a.nr_seq_movto	= b.nr_sequencia
and	a.nr_sequencia	= nr_seq_parcela_p;

if (nr_seq_grupo_p IS NOT NULL AND nr_seq_grupo_p::text <> '') then

	select	coalesce(max(a.ie_ajustar_valor),'N')
	into STRICT	ie_ajustar_valor_w
	from	bandeira_cartao_cr_regra a
	where	a.nr_seq_grupo		= nr_seq_grupo_p
	and	clock_timestamp()			between coalesce(a.dt_inicio_vigencia,clock_timestamp()) and coalesce(a.dt_fim_vigencia,clock_timestamp())
	and	coalesce(a.cd_estabelecimento,coalesce(cd_estabelecimento_w,0))	= coalesce(cd_estabelecimento_w,0);

else

	select	coalesce(max(a.ie_ajustar_valor),'N')
	into STRICT	ie_ajustar_valor_w
	from	bandeira_cartao_cr_regra a
	where	a.nr_seq_bandeira	= nr_seq_bandeira_w
	and	clock_timestamp()			between coalesce(a.dt_inicio_vigencia,clock_timestamp()) and coalesce(a.dt_fim_vigencia,clock_timestamp())
	and	coalesce(a.cd_estabelecimento,coalesce(cd_estabelecimento_w,0))	= coalesce(cd_estabelecimento_w,0);

end if;

/* ajustar o valor da parcela conforme a diferença com relação ao extrato */

if (ie_ajustar_valor_w	= 'S') then

	/* ratear o valor para a parcela */

	select	coalesce(max(coalesce(a.vl_liquido,a.vl_parcela)),0)
	into STRICT	vl_extrato_w
	from	extrato_cartao_cr_movto a
	where	a.nr_sequencia	= nr_seq_ext_movto_p;

	if (vl_extrato_w	= 0) then

		select	sum(coalesce(c.vl_liquido,c.vl_parcela))
		into STRICT	vl_extrato_w
		from	extrato_cartao_cr_movto c,
			ext_cartao_cr_movto_concil b,
			extrato_cartao_cr_parcela a
		where	b.nr_seq_ext_movto	= c.nr_sequencia
		and	a.nr_sequencia		= b.nr_seq_extrato_parcela
		and	a.nr_sequencia		= nr_seq_extrato_parcela_w;

		if (coalesce(vl_extrato_w,0)	= 0) then

			select	sum(coalesce(b.vl_liquido,b.vl_parcela))
			into STRICT	vl_extrato_w
			from	extrato_cartao_cr_movto b,
				extrato_cartao_cr_parcela a
			where	a.nr_sequencia	= b.nr_seq_extrato_parcela
			and	a.nr_sequencia	= nr_seq_extrato_parcela_w;

		end if;

	end if;

	vl_ajuste_w	:= (vl_liquido_w * coalesce(vl_ajuste_p,0)) / (coalesce(vl_extrato_w,1) + (coalesce(vl_ajuste_p,0) * -1));

	if (coalesce(vl_ajuste_w,0)	<> 0) then

		update	movto_cartao_cr_parcela
		set	vl_original		= vl_liquido_w,
			vl_despesa		= coalesce(vl_despesa,0) - coalesce(vl_ajuste_w,0),
			vl_liquido		= vl_liquido_w + coalesce(vl_ajuste_w,0),
			vl_saldo_liquido	= coalesce(vl_saldo_liquido,0) + coalesce(vl_ajuste_w,0),
			dt_atualizacao		= clock_timestamp(),
			nm_usuario		= nm_usuario_p
		where	nr_sequencia		= nr_seq_parcela_p;

	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ajustar_parcela_cartao_cr ( nr_seq_parcela_p bigint, nr_seq_grupo_p bigint, vl_ajuste_p bigint, nm_usuario_p text, nr_seq_ext_movto_p bigint) FROM PUBLIC;

