SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ajustar_prescr_material_sne ( nr_prescricao_p bigint, nr_sequencia_p bigint) AS $body$
DECLARE


qt_horas_estabilidade_w		prescr_material.qt_horas_estabilidade%type;
qt_operacao_w			double precision;
nr_seq_material_w		prescr_material.nr_sequencia%type;
ie_acm_w			prescr_material.ie_acm%type;
ie_se_necessario_w		prescr_material.ie_se_necessario%type;
nr_ocorrencia_w			prescr_material.nr_ocorrencia%type;
ie_urgencia_w			prescr_material.ie_urgencia%type;
ie_suspenso_w			prescr_material.ie_suspenso%type;
cd_unidade_medida_dose_w	prescr_material.cd_unidade_medida_dose%type;
nr_seq_recomendacao_w		prescr_material.nr_seq_recomendacao%type;
ds_horarios_novo_w		varchar(2000);
cd_um_dose_w			prescr_material.cd_unidade_medida_dose%type;
qt_dispensar_w			prescr_material.qt_total_dispensar%type;
hr_prim_horario_w		prescr_material.hr_prim_horario%type;
cd_intervalo_w			prescr_material.cd_intervalo%type;
qt_total_disp_w			prescr_material.qt_dia_prim_hor%type;
qt_dia_prim_hor_w		prescr_material.qt_dia_prim_hor%type;
cd_material_w			prescr_material.cd_material%type;
qt_unitaria_w			prescr_material.qt_unitaria%type;
qt_material_w			prescr_material.qt_material%type;
ie_origem_inf_w			prescr_material.ie_origem_inf%type;
ie_via_aplicacao_w		prescr_material.ie_via_aplicacao%type;
ds_erro_w			varchar(2000);
cd_unid_med_dose_w		prescr_material.cd_unidade_medida_dose%type;
cd_kit_material_w		prescr_material.cd_kit_material%type;
nr_ocorrencia_kit_w		prescr_material.nr_ocorrencia%type;
cd_intervalo_novo_w		varchar(7);
cd_intervalo_cad_w		varchar(7);
cd_intervalo_kit_w		componente_kit.cd_intervalo%type;
ie_regra_disp_w			varchar(1);
ds_horarios_kit_w		prescr_material.ds_horarios%type;
ds_horarios_medic_w		prescr_material.ds_horarios%type;

C01 CURSOR FOR
	SELECT	coalesce(a.nr_sequencia,0),
		coalesce(a.cd_material,0),
		coalesce(a.qt_unitaria,0),
		coalesce(a.qt_material,0),
		coalesce(a.ie_origem_inf,'1'),
		a.ie_via_aplicacao,
		a.cd_intervalo,
		a.cd_unidade_medida_dose,
		a.cd_kit_material,
		a.nr_ocorrencia,
		a.ds_horarios
	from	material b,
		prescr_material a,
		prescr_medica x
	where	a.cd_material = b.cd_material
	and	(a.cd_material IS NOT NULL AND a.cd_material::text <> '')
	and	a.nr_seq_kit = nr_sequencia_p
	and	x.nr_prescricao = a.nr_prescricao
	and	x.nr_prescricao = nr_prescricao_p;


BEGIN
begin
select	coalesce(max(cd_intervalo),obter_interv_prescr_padrao(wheb_usuario_pck.get_cd_estabelecimento)),
	coalesce(max(ie_acm),'N'),
	coalesce(max(ie_se_necessario),'N'),
	coalesce(max(nr_ocorrencia),1),
	coalesce(max(ie_urgencia),'N'),
	coalesce(max(ie_suspenso),'N'),
	coalesce(max(cd_unidade_medida_dose),''),
	max(hr_prim_horario),
	max(qt_dia_prim_hor),
	max(ds_horarios)
into STRICT	cd_intervalo_w,
	ie_acm_w,
	ie_se_necessario_w,
	nr_ocorrencia_w,
	ie_urgencia_w,
	ie_suspenso_w,
	cd_unidade_medida_dose_w,
	hr_prim_horario_w,
	qt_dia_prim_hor_w,
	ds_horarios_medic_w
from	prescr_material
where	(cd_material IS NOT NULL AND cd_material::text <> '')
and	nr_sequencia  = nr_sequencia_p
and	nr_prescricao = nr_prescricao_p;

open C01;
loop
fetch C01 into
	nr_seq_material_w,
	cd_material_w,
	qt_unitaria_w,
	qt_material_w,
	ie_origem_inf_w,
	ie_via_aplicacao_w,
	cd_intervalo_cad_w,
	cd_unid_med_dose_w,
	cd_kit_material_w,
	nr_ocorrencia_kit_w,
	ds_horarios_kit_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	cd_intervalo_novo_w := cd_intervalo_w;
	qt_operacao_w := nr_ocorrencia_w;
	ds_horarios_novo_w := ds_horarios_medic_w;

	if (cd_kit_material_w IS NOT NULL AND cd_kit_material_w::text <> '') then
		select	max(cd_intervalo)
		into STRICT	cd_intervalo_kit_w
		from	componente_kit
		where	cd_kit_material = cd_kit_material_w
		and	cd_material = cd_material_w;

		if (cd_intervalo_kit_w IS NOT NULL AND cd_intervalo_kit_w::text <> '') and (cd_intervalo_kit_w = cd_intervalo_cad_w) then
			cd_intervalo_novo_w := cd_intervalo_cad_w;
			qt_operacao_w := nr_ocorrencia_kit_w;
			ds_horarios_novo_w := ds_horarios_kit_w;
		end if;
	end if;

	if (cd_kit_material_w IS NOT NULL AND cd_kit_material_w::text <> '') then
		cd_um_dose_w := cd_unid_med_dose_w;
	else
		cd_um_dose_w := cd_unidade_medida_dose_w;
	end if;

	SELECT * FROM obter_quant_dispensar(wheb_usuario_pck.get_cd_estabelecimento, cd_material_w, nr_prescricao_p, nr_seq_material_w, cd_intervalo_novo_w, ie_via_aplicacao_w, qt_unitaria_w, null, qt_operacao_w, null, ie_origem_inf_w, cd_um_dose_w, 1, qt_material_w, qt_dispensar_w, ie_regra_disp_w, ds_erro_w, ie_se_necessario_w, ie_acm_w) INTO STRICT qt_material_w, qt_dispensar_w, ie_regra_disp_w, ds_erro_w;

	update	prescr_material
	set	nr_ocorrencia = qt_operacao_w,
		qt_total_dispensar = qt_dispensar_w,
		qt_unitaria = qt_unitaria_w,
		qt_material = qt_material_w,
		cd_intervalo = cd_intervalo_novo_w,
		ie_acm = ie_acm_w,
		ie_se_necessario = ie_se_necessario_w,
		ie_utiliza_kit = 'N',
		ie_urgencia = ie_urgencia_w,
		ie_suspenso = ie_suspenso_w,
		ie_regra_disp = CASE WHEN coalesce(ie_regra_disp,'X')='D' THEN  ie_regra_disp  ELSE ie_regra_disp_w END ,
		qt_dia_prim_hor = qt_dia_prim_hor_w,
		ds_horarios = CASE WHEN ie_se_necessario_w='S' THEN 'SN'  ELSE coalesce(ds_horarios_novo_w,ds_horarios) END ,
		hr_prim_horario = CASE WHEN ie_se_necessario_w='S' THEN hr_prim_horario_w  ELSE coalesce(hr_prim_horario_w,hr_prim_horario) END
	where	nr_sequencia = nr_seq_material_w
	and	nr_prescricao = nr_prescricao_p;
end loop;
close C01;
exception when others then
	ds_erro_w := substr(to_char(sqlerrm),1,2000);
	CALL gerar_log_prescricao(nr_prescricao_p, nr_sequencia_p, 1, null, null, ds_erro_w, wheb_usuario_pck.get_nm_usuario, 59068, 'N');
end;

begin
	qt_horas_estabilidade_w	:= obter_estabilidade_mat_prescr(nr_prescricao_p, nr_sequencia_p);
exception when others then
	qt_horas_estabilidade_w	:= null;
end;

update	prescr_material
set	qt_horas_estabilidade	= qt_horas_estabilidade_w
where	1 = 1
and	nr_sequencia		= nr_sequencia_p
and	nr_prescricao		= nr_prescricao_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ajustar_prescr_material_sne ( nr_prescricao_p bigint, nr_sequencia_p bigint) FROM PUBLIC;

